import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import "../styles/Payment.css";

const Payment = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!state?.selectedItems?.length && !state?.item) {
      setError("Không có sản phẩm nào để thanh toán");
      setTimeout(() => navigate("/cart"), 2000);
    }
  }, [state, navigate]);

  const handlePayment = async () => {
    const items = state?.selectedItems || (state?.item ? [state.item] : []);
    const total = state?.total || 0;
    const address = state?.selectedAddress || {};

    if (!items.length || total <= 0) {
      setError("Giỏ hàng trống");
      return;
    }

    if (!address.fullName || !address.phone || !address.address) {
      setError("Vui lòng nhập đầy đủ địa chỉ");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const orderInfo = items.map(item => 
        `${item.name} (${item.size}) - ${item.quantity}x${item.price}`
      ).join(", ");

      const response = await axios.post(
        "http://localhost:5001/create_payment",
        {
          amount: total,
          orderInfo,
          items,
          address
        },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );

      if (response.data.status === "success") {
        window.location.href = response.data.url;
      } else {
        setError("Không thể tạo liên kết thanh toán");
      }
    } catch (err) {
      console.error("Lỗi thanh toán:", err);
      if (err.response?.status === 401) {
        logout();
        navigate("/login");
      } else {
        setError(err.response?.data?.error || "Lỗi hệ thống");
      }
    } finally {
      setLoading(false);
    }
  };

  if (!state?.selectedItems?.length && !state?.item) {
    return (
      <div className="payment-container">
        <p className="error">{error || "Đang chuyển hướng..."}</p>
      </div>
    );
  }

  const items = state.selectedItems || [state.item];
  const total = state.total;
  const address = state.selectedAddress;

  return (
    <div className="payment-container">
      <h2>Thanh toán</h2>
      
      <div className="order-summary">
        <h3>Đơn hàng của bạn</h3>
        {items.map(item => (
          <div key={`${item.id}-${item.size}`} className="order-item">
            <img src={item.image} alt={item.name} />
            <div>
              <p>{item.name} ({item.size})</p>
              <p>{item.quantity} x {item.price.toLocaleString()}đ</p>
            </div>
          </div>
        ))}
        <div className="order-total">
          Tổng cộng: <strong>{total.toLocaleString()}đ</strong>
        </div>
      </div>

      <div className="shipping-info">
        <h3>Địa chỉ nhận hàng</h3>
        <p>{address.fullName}</p>
        <p>{address.phone}</p>
        <p>{address.address}</p>
      </div>

      {error && <p className="error">{error}</p>}

      <button 
        onClick={handlePayment} 
        disabled={loading}
        className="payment-button"
      >
        {loading ? "Đang xử lý..." : "Thanh toán VNPay"}
      </button>
    </div>
  );
};

export default Payment;
