import React, { useState } from 'react';
import '../styles/cartsidebar.css';
function CartSidebar() {
    const [cartItems, setCartItems] = useState([]);

    window.addToCart = (name, price, image) => {
        setCartItems(prev => [...prev, { name, price, image }]);
    };

    const removeFromCart = (index) => {
        setCartItems(prev => prev.filter((_, i) => i !== index));
    };

    const total = cartItems.reduce((sum, item) => sum + item.price, 0);

    return (
        <div id="cart-sidebar">
            <h2>Gi·ªè h√†ng üõí</h2>
            <p id="cart-count">S·ªë l∆∞·ª£ng: {cartItems.length}</p>
            <ul id="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index}>
                        {item.name} - {item.price.toLocaleString()} VND
                        <button onClick={() => removeFromCart(index)}>X√≥a</button>
                    </li>
                ))}
            </ul>
            <p>T·ªïng ti·ªÅn: <span id="cart-total">{total.toLocaleString()} VND</span></p>
            <button id="checkout-btn" onClick={() => window.location.href = 'https://your-payment-link.com'}>Thanh to√°n</button>
        </div>
    );
}

export default CartSidebar;
