import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        const savedCart = localStorage.getItem('cartItems');
        return savedCart ? JSON.parse(savedCart) : [];
    });

    const [isOpen, setIsOpen] = useState(false);

    // ✅ Hỗ trợ thêm sản phẩm với trọng lượng "25" (0.25kg), "5" (0.5kg), "1" (1kg)
    const addToCart = (name, price, image, weight) => {
        setCartItems(prev => {
            const existingItem = prev.find(item => item.name === name && item.weight === weight);
            let updatedCart;
            if (existingItem) {
                updatedCart = prev.map(item =>
                    item.name === name && item.weight === weight
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                );
            } else {
                updatedCart = [...prev, { name, price, image, weight, quantity: 1, selected: true }];
            }
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
        setIsOpen(true);
    };

    const removeFromCart = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.filter((_, i) => i !== index);
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    const updateQuantity = (index, amount) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, quantity: Math.max(1, item.quantity + amount) } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleItemSelection = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, selected: !item.selected } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const selectAll = () => {
        setCartItems(prev => {
            const updatedCart = prev.map(item => ({ ...item, selected: true }));
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const deselectAll = () => {
        setCartItems(prev => {
            const updatedCart = prev.map(item => ({ ...item, selected: false }));
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
    const total = cartItems.reduce(
        (sum, item) => (item.selected ? sum + item.price * item.quantity : sum),
        0
    );

    // ✅ Chỉ cập nhật `localStorage` khi `cartItems` thay đổi
    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider
            value={{
                cartItems,
                addToCart,
                removeFromCart,
                isOpen,
                toggleCart,
                total,
                totalItems,
                updateQuantity,
                toggleItemSelection,
                selectAll,
                deselectAll,
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
