import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Để điều hướng khi nhấn vào sản phẩm
import '../styles/menu.css';
import { fetchMenuItems } from '../services/menuService';
import { useCart } from '../context/CartContext';

function Menu() {
    const [activeCategory, setActiveCategory] = useState('fruit');
    const [menuItems, setMenuItems] = useState([]);
    const { addToCart } = useCart();
    const navigate = useNavigate(); // Hook điều hướng

    // State để quản lý modal chọn khối lượng
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [selectedWeight, setSelectedWeight] = useState("25"); // Mặc định 0.25kg

    useEffect(() => {
        fetchMenuItems(activeCategory).then(setMenuItems);
    }, [activeCategory]);

    // Xử lý khi nhấn vào nút giỏ hàng
    const handleAddToCart = (item) => {
        setSelectedProduct(item);
        setSelectedWeight("25"); // Reset về 0.25kg mỗi khi mở modal
    };

    // Xác nhận thêm vào giỏ hàng
    const confirmAddToCart = () => {
        if (selectedProduct) {
            addToCart({
                name: selectedProduct.name,
                price: selectedProduct.prices["0"]?.[selectedWeight] || selectedProduct.prices["1"],
                image: selectedProduct.image,
                weight: selectedWeight,
            });
            setSelectedProduct(null); // Đóng modal
        }
    };

    return (
        <section className="menu section-padding" id="dactrung">
            <div className="container">
                <div className="row">
                    <div className="section-title">
                        <h2 data-title="Món Đặc Trưng">Thực đơn của chúng tôi</h2>
                    </div>
                </div>

                <div className="row">
                    <div className="menu-title">
                        <h2>Ẩm thực Thái Lan mang một sự quyến rũ đặc biệt...</h2>
                        <nav className="menu-nav">
                            <ul>
                                {["fruit", "seafood", "meat", "nut"].map((category) => (
                                    <li key={category}>
                                        <button
                                            onClick={() => setActiveCategory(category)}
                                            className={`menu-button ${activeCategory === category ? 'active' : ''}`}
                                        >
                                            {category === "fruit" && "Trái Cây Sấy"}
                                            {category === "seafood" && "Hải Sản Sấy"}
                                            {category === "meat" && "Thịt Sấy"}
                                            {category === "nut" && "Hạt Sấy"}
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </nav>
                    </div>
                </div>

                <div className="row justify-content">
                    <div id={activeCategory} className="menu-content show">
                        {menuItems.map((item) => (
                            <div className="row food-items" key={item._id}>
                                {/* Nhấn vào ảnh hoặc tên sản phẩm để điều hướng đến trang chi tiết */}
                                <div 
                                    className="row food-item" 
                                    onClick={() => navigate(`/${item.category}/${item._id}`)}
                                    style={{ cursor: "pointer" }}
                                >
                                    <img src={item.image} alt={item.name} />
                                    <h2>{item.name}</h2>
                                </div>
                                <div className="food-price">
                                    <p>
                                        {item.prices?.["0"]?.["25"]
                                            ? `${item.prices["0"]["25"].toLocaleString()} VND / 0.25kg`
                                            : "Giá chưa cập nhật"}
                                    </p>
                                </div>
                                <button className="add-to-cart-btn" onClick={() => handleAddToCart(item)}>
                                    🛒
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Modal chọn khối lượng */}
            {selectedProduct && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Chọn khối lượng</h2>
                        <p>{selectedProduct.name}</p>
                        <img src={selectedProduct.image} alt={selectedProduct.name} />
                        
                        <label>Khối lượng: </label>
                        <select value={selectedWeight} onChange={(e) => setSelectedWeight(e.target.value)}>
                            <option value="25">0.25kg</option>
                            <option value="5">0.5kg</option>
                            <option value="1">1kg</option>
                        </select>

                        <p>
                            Giá: {selectedProduct.prices && selectedProduct.prices["0"]?.[selectedWeight]
                                ? selectedProduct.prices["0"][selectedWeight].toLocaleString()
                                : selectedProduct.prices["1"]?.toLocaleString() || "Chưa có giá"} VND
                        </p>

                        <button onClick={confirmAddToCart}>Xác nhận</button>
                        <button onClick={() => setSelectedProduct(null)}>Hủy</button>
                    </div>
                </div>
            )}
        </section>
    );
}

export default Menu;
