import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useAuth } from "../context/AuthContext";
import ShippingAddressForm from "./ShippingAddressForm";
import AddressService from "../services/AddressService";
import "../styles/Cart.css";

const Cart = () => {
  const {
    cartItems,
    total,
    totalItems,
    increaseQuantity,
    decreaseQuantity,
    removeFromCart,
    selectAll,
    toggleItemSelection,
    displaySize,
  } = useCart();

  const { user } = useAuth();
  const navigate = useNavigate();
  const [isAddressFormOpen, setIsAddressFormOpen] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchAddresses = async () => {
      const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
      setLoading(true);
      try {
        const addressList = await AddressService.getAddresses(userId); // Không cần token
        setAddresses(addressList || []);
        const defaultAddress = addressList.find((addr) => addr.isDefault) || addressList[0];
        if (defaultAddress) {
          setSelectedAddress({
            fullName: defaultAddress.fullName,
            phone: defaultAddress.phone,
            address: `${defaultAddress.detail}, ${defaultAddress.ward}, ${defaultAddress.district}, ${defaultAddress.province}`,
          });
        }
      } catch (error) {
        console.error("Lỗi khi lấy địa chỉ:", error);
        setError(`Không thể tải danh sách địa chỉ: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchAddresses();
  }, [user]);

  const handleAddressSelect = (address) => {
    setSelectedAddress({
      fullName: address.fullName,
      phone: address.phone,
      address: `${address.detail}, ${address.ward}, ${address.district}, ${address.province}`,
    });
  };

  const handleNewAddressAdded = async (newAddress) => {
    setSelectedAddress({
      fullName: newAddress.fullName,
      phone: newAddress.phone,
      address: newAddress.address,
    });
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    try {
      const updatedAddresses = await AddressService.getAddresses(userId);
      setAddresses(updatedAddresses || []);
    } catch (error) {
      setError(`Không thể cập nhật danh sách địa chỉ: ${error.message}`);
    }
    setIsAddressFormOpen(false);
  };

  const handleDeleteAddress = async (addressId) => {
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    try {
      await AddressService.deleteAddress(addressId, userId); // Không cần token
      const updatedAddresses = addresses.filter((addr) => addr._id !== addressId);
      setAddresses(updatedAddresses);
      if (
        selectedAddress?.address ===
        addresses.find((addr) => addr._id === addressId)?.address
      ) {
        setSelectedAddress(null);
      }
    } catch (error) {
      setError(`Không thể xóa địa chỉ: ${error.message}`);
    }
  };

  const handleSelectAllChange = (e) => {
    selectAll(e.target.checked);
  };

  const handleCheckout = () => {
    if (total <= 0) {
      alert("Giỏ hàng của bạn trống hoặc chưa chọn sản phẩm nào!");
      return;
    }
    if (!selectedAddress) {
      alert("Vui lòng chọn hoặc thêm địa chỉ giao hàng để tiếp tục!");
      return;
    }
    const selectedItems = cartItems.filter((item) => item.selected);
    console.log("Cart total before checkout:", total);
    console.log("Selected items:", selectedItems);
    navigate("/payment", {
      state: {
        selectedAddress,
        selectedItems,
        total,
      },
    });
  };

  const handleViewProduct = (productId, category) => {
    const productPath = category ? `/${category}/${productId}` : `/product/${productId}`;
    navigate(productPath);
  };

  return (
    <div className="cart-container">
      <div className="cart-header">
        <div className="shop-logo">
          <h1>Hương Việt Tinh</h1>
          <p>Giỏ Hàng ({totalItems} sản phẩm)</p>
        </div>
      </div>

      <div className="address-section">
        <h3>Địa chỉ giao hàng</h3>
        {loading ? (
          <p>Đang tải địa chỉ...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : addresses.length === 0 ? (
          <div>
            <p>Chưa có địa chỉ giao hàng. Vui lòng thêm địa chỉ để tiếp tục đặt hàng.</p>
            <button
              onClick={() => setIsAddressFormOpen(true)}
              className="add-address-btn"
            >
              Thêm địa chỉ giao hàng
            </button>
          </div>
        ) : (
          <div className="address-list">
            {selectedAddress ? (
              <div className="selected-address">
                <p>
                  <strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})
                </p>
                <p>{selectedAddress.address}</p>
                <button onClick={() => setIsAddressFormOpen(true)} className="change-address-btn">
                  Thay đổi
                </button>
              </div>
            ) : (
              <p>Chưa chọn địa chỉ giao hàng.</p>
            )}
            {!isAddressFormOpen && (
              <div className="address-options">
                <h4>Chọn địa chỉ:</h4>
                {addresses.map((address) => (
                  <div key={address._id} className="address-item">
                    <div onClick={() => handleAddressSelect(address)} className="address-content">
                      <p>
                        <strong>{address.fullName}</strong> ({address.phone})
                      </p>
                      <p>{`${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</p>
                    </div>
                    <button
                      onClick={() => handleDeleteAddress(address._id)}
                      className="delete-address-btn"
                    >
                      Xóa
                    </button>
                  </div>
                ))}
                <button
                  onClick={() => setIsAddressFormOpen(true)}
                  className="add-address-btn"
                >
                  Thêm địa chỉ mới
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="cart-items">
        <div className="cart-item-header">
          <input
            type="checkbox"
            onChange={handleSelectAllChange}
            checked={cartItems.length > 0 && cartItems.every((item) => item.selected)}
          />
          <span>Sản Phẩm</span>
          <span>Phân Loại Hàng</span>
          <span>Đơn Giá</span>
          <span>Số Lượng</span>
          <span>Số Tiền</span>
          <span>Thao Tác</span>
        </div>

        {Array.isArray(cartItems) && cartItems.length === 0 ? (
          <p className="empty-cart">Giỏ hàng của bạn đang trống.</p>
        ) : (
          cartItems.map((item) => (
            <div key={`${item.productId}-${item.size}`} className="cart-item">
              <input
                type="checkbox"
                checked={item.selected}
                onChange={(e) => {
                  e.stopPropagation();
                  toggleItemSelection(item.productId, item.size);
                }}
              />
              <div
                className="cart-item-details"
                onClick={() => handleViewProduct(item.productId, item.category)}
                style={{ cursor: "pointer" }}
              >
                <img src={item.image} alt={item.name} className="cart-item-image" />
                <div className="cart-item-info">
                  <p>{item.name}</p>
                  <p className="cart-item-classification">
                    Phân Loại Hàng: {item.color || item.category || "Không xác định"},{" "}
                    {displaySize(item.size)}
                  </p>
                </div>
              </div>
              <div className="cart-item-classification">
                {item.color || item.category || "Không xác định"}, {displaySize(item.size)}
              </div>
              <div className="cart-item-price">{item.price.toLocaleString()}đ</div>
              <div className="cart-item-quantity">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    decreaseQuantity(item.productId, item.size);
                  }}
                >
                  -
                </button>
                <span>{item.quantity}</span>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    increaseQuantity(item.productId, item.size);
                  }}
                >
                  +
                </button>
              </div>
              <div className="cart-item-total">{(item.price * item.quantity).toLocaleString()}đ</div>
              <div className="cart-item-actions">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromCart(item.productId, item.size);
                  }}
                >
                  Xóa
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="cart-summary">
        <div className="summary-details">
          <p>
            Tổng thanh toán ({cartItems.filter((item) => item.selected).length} sản phẩm):{" "}
            <span>{total.toLocaleString()}đ</span>
          </p>
        </div>
        <button
          className="checkout-btn"
          onClick={handleCheckout}
          disabled={total === 0 || !selectedAddress}
        >
          Đặt Hàng
        </button>
      </div>

      {isAddressFormOpen && (
        <div className="address-form-overlay">
          <ShippingAddressForm
            onAddressSelect={handleNewAddressAdded}
            onClose={() => setIsAddressFormOpen(false)}
          />
        </div>
      )}
    </div>
  );
};

export default Cart;
