import '../styles/menu.css'; // Import file CSS
import React, { useState, useEffect } from 'react';
import { fetchMenuItems } from '../services/menuService';

function Menu({ addToCart }) {
    const [activeCategory, setActiveCategory] = useState('fruit');
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        fetchMenuItems(activeCategory).then(setMenuItems);
    }, [activeCategory]);

    return (
        <section className="menu section-padding" id="dactrung">
            <div className="container">
                <div className="row">
                    <div className="section-title">
                        <h2 data-title="Món Đặc Trưng">Thực đơn của chúng tôi</h2>
                    </div>
                </div>

                {/* Menu Nav */}
                <div className="row">
                    <div className="menu-title">
                        <h2>Ẩm thực Thái Lan mang một sự quyến rũ đặc biệt...</h2>
                        <nav className="menu-nav">
                            <ul>
                                <li>
                                    <button 
                                        onClick={() => setActiveCategory('fruit')} 
                                        className={`menu-button ${activeCategory === 'fruit' ? 'active' : ''}`}
                                    >
                                        Bữa Trưa
                                    </button>
                                </li>
                                <li>
                                    <button 
                                        onClick={() => setActiveCategory('seafood')} 
                                        className={`menu-button ${activeCategory === 'seafood' ? 'active' : ''}`}
                                    >
                                        Bữa Tối
                                    </button>
                                </li>
                                <li>
                                    <button 
                                        onClick={() => setActiveCategory('meat')} 
                                        className={`menu-button ${activeCategory === 'meat' ? 'active' : ''}`}
                                    >
                                        Khai Vị
                                    </button>
                                </li>
                                <li>
                                    <button 
                                        onClick={() => setActiveCategory('nut')} 
                                        className={`menu-button ${activeCategory === 'nut' ? 'active' : ''}`}
                                    >
                                        Đồ Uống
                                    </button>
                                </li>
                            </ul>
                        </nav>
                    </div>
                </div>

                {/* Hiển thị danh mục hiện tại */}
                <div className="row justify-content">
                    <div id={activeCategory} className="menu-content show">
                        {menuItems.map((item, index) => (
                            <div className="row food-items" key={index}>
                                <div className="row food-item">
                                    <img src={item.image} alt={item.name} />
                                    <h2>{item.name}</h2>
                                </div>
                                <div className="food-price">
                                    <p>{item.price.toLocaleString()} VND</p>
                                </div>
                                <button 
                                    className="add-to-cart-btn"
                                    onClick={() => addToCart(item.name, item.price, item.image)}
                                >
                                    🛒
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Menu;
