// context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axios.get('http://localhost:5000/api/users/me', {
                headers: { Authorization: `Bearer ${token}` }
            })
                .then(response => {
                    setUser(response.data);
                    setLoading(false);
                })
                .catch(error => {
                    console.error('Lỗi khi lấy thông tin user:', error);
                    localStorage.removeItem('token');
                    setUser(null);
                    setLoading(false);
                });
        } else {
            setLoading(false);
        }
    }, []);

    const login = async (email, password) => {
        try {
            // Kiểm tra email và password trước khi gửi
            if (!email || !password) {
                throw new Error('Vui lòng nhập email và mật khẩu.');
            }
            const response = await axios.post('http://localhost:5000/api/users/login', { email, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token);
            setUser(user);
        } catch (error) {
            // Ném lỗi với thông điệp chi tiết từ server
            throw new Error(error.response?.data?.message || 'Lỗi đăng nhập');
        }
    };

    const register = async (username, email, phone, password) => {
        try {
            if (!username || !email || !phone || !password) {
                throw new Error('Vui lòng nhập đầy đủ thông tin.');
            }
            const response = await axios.post('http://localhost:5000/api/users/register', { username, email, phone, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token);
            setUser(user);
        } catch (error) {
            throw new Error(error.response?.data?.message || 'Lỗi đăng ký');
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, loading, login, register, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
