import React, { useState } from 'react';
import AOS from 'aos';

function Header() {
    const [menuActive, setMenuActive] = useState(false);

    React.useEffect(() => {
        AOS.init();
    }, []);

    return (
        <section className={`top ${menuActive ? 'active' : ''}`}>
            <div className="container">
                <div className="row justify-content">
                    <div className="logo">
                        <img src="/images/TMKLogo2020Xanh.png" alt="Logo" />
                    </div>
                    <div className={`menu-items ${menuActive ? 'active' : ''}`}>
                        <ul>
                            <li className="menu-item" style={{ '--x': 1 }}><a href="#home">Trang Chủ</a></li>
                            <li className="menu-item" style={{ '--x': 2 }}><a href="#about">Về chúng tôi</a></li>
                            <li className="menu-item"><a href="/Booking/booking.html" rel="noopener" target="_blank">Đặt Bàn</a></li>
                            <li className="menu-item" style={{ '--x': 3 }}><a href="#dactrung">Món đặc trưng</a></li>
                            <li className="menu-item" style={{ '--x': 4 }}><a href="#feedback">Phản hồi</a></li>
                            <li className="menu-item" style={{ '--x': 6 }}><a href="#employee">Nhân sự</a></li>
                            <li className="menu-item" style={{ '--x': 7 }}><a href="#contact">Liên hệ</a></li>
                        </ul>
                    </div>
                    <div className={`menubar ${menuActive ? 'active' : ''}`} onClick={() => setMenuActive(!menuActive)}>
                        <span></span>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Header;
