// src/components/ProductItem.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ProductItem.css';

// Hàm ánh xạ size từ giao diện sang database
const mapSizeToDatabase = (size) => {
  switch (size) {
    case '0.25': return '25';
    case '0.5': return '5';
    case '1': return '1';
    default: return size;
  }
};

// Hàm ánh xạ size từ database sang giao diện
const mapSizeToDisplay = (size) => {
  switch (size) {
    case '25': return '0.25kg';
    case '5': return '0.5kg';
    case '1': return '1kg';
    default: return `${size}kg`;
  }
};

const ProductItem = ({ product, addToCart, selectedAddress }) => {
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState('0.25');
  const [error, setError] = useState(null);

  const handleAddToCart = () => {
    const dbSize = mapSizeToDatabase(size);
    const price = product.prices?.[size === '1' ? '1' : '0']?.[dbSize];
    if (!price) {
      setError('Giá sản phẩm chưa được cập nhật. Vui lòng thử lại sau.');
      return;
    }
    addToCart({
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: size,
      quantity: quantity,
    });
    setError(null);
  };

  const handleBuyNow = () => {
    const dbSize = mapSizeToDatabase(size);
    const price = product.prices?.[size === '1' ? '1' : '0']?.[dbSize];
    if (!price) {
      setError('Giá sản phẩm chưa được cập nhật. Vui lòng thử lại sau.');
      return;
    }
    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: size,
      quantity: quantity,
      selected: true,
    };
    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: price * quantity,
        selectedAddress: selectedAddress,
      },
    });
    setError(null);
  };

  const dbSize = mapSizeToDatabase(size);
  const currentPrice = product.prices?.[size === '1' ? '1' : '0']?.[dbSize];
  const displaySize = mapSizeToDisplay(dbSize);

  return (
    <div className="product-item">
      <img src={product.image} alt={product.name} className="product-image" />
      <h3>{product.name}</h3>
      <div className="rating">
        <span>★ {product.rating || 'Chưa có đánh giá'} (0 lượt bán)</span>
      </div>
      {error && <p className="error-message">{error}</p>}
      <p className="price">
        {currentPrice
          ? `${currentPrice.toLocaleString()} VND / ${displaySize}`
          : `Giá chưa cập nhật ${displaySize}`}
      </p>
      <div className="size-selection">
        <label>Chọn trọng lượng:</label>
        <button onClick={() => setSize('0.25')} className={size === '0.25' ? 'active' : ''}>0.25kg</button>
        <button onClick={() => setSize('0.5')} className={size === '0.5' ? 'active' : ''}>0.5kg</button>
        <button onClick={() => setSize('1')} className={size === '1' ? 'active' : ''}>1kg</button>
      </div>
      <div className="quantity-selection">
        <label>Số lượng:</label>
        <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
        <span>{quantity}</span>
        <button onClick={() => setQuantity(quantity + 1)}>+</button>
      </div>
      <div className="actions">
        <button onClick={handleAddToCart} disabled={!currentPrice}>
          Thêm vào giỏ hàng
        </button>
        <button onClick={handleBuyNow} className="buy-now" disabled={!currentPrice}>
          Mua ngay
        </button>
      </div>
    </div>
  );
};

export default ProductItem;
