import '../styles/menu.css'; // ƒê·∫£m b·∫£o import file CSS c·ªßa b·∫°n
import React, { useState, useEffect } from 'react';
import  {fetchMenuItems}  from '../services/menuService';

function Menu({ addToCart }) {
    const [activeCategory, setActiveCategory] = useState('lunch');
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        fetchMenuItems(activeCategory).then(setMenuItems);
    }, [activeCategory]);

    return (
        <div>
            <nav>
                <button onClick={() => setActiveCategory('lunch')}>B·ªØa Tr∆∞a</button>
                <button onClick={() => setActiveCategory('diner')}>B·ªØa T·ªëi</button>
                <button onClick={() => setActiveCategory('appetizer')}>Khai V·ªã</button>
                <button onClick={() => setActiveCategory('drinks')}>ƒê·ªì U·ªëng</button>
            </nav>
            <div>
                {menuItems.map((item, index) => (
                    <div key={index}>
                        <img src={item.image} alt={item.name} />
                        <h2>{item.name}</h2>
                        <p>{item.price.toLocaleString()} VND</p>
                        <button onClick={() => addToCart(item.name, item.price, item.image)}>üõí</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Menu;
