// services/AddressService.js
const ADDRESS_STORAGE_KEY = 'user_addresses';

const AddressService = {
    // Lấy danh sách địa chỉ từ localStorage
    getAddresses() {
        const addresses = localStorage.getItem(ADDRESS_STORAGE_KEY);
        return addresses ? JSON.parse(addresses) : [];
    },

    // Thêm một địa chỉ mới
    addAddress(address) {
        const addresses = this.getAddresses();
        addresses.push(address);
        localStorage.setItem(ADDRESS_STORAGE_KEY, JSON.stringify(addresses));
        return addresses;
    },

    // Cập nhật một địa chỉ (dựa trên index)
    updateAddress(index, updatedAddress) {
        const addresses = this.getAddresses();
        if (index >= 0 && index < addresses.length) {
            addresses[index] = updatedAddress;
            localStorage.setItem(ADDRESS_STORAGE_KEY, JSON.stringify(addresses));
        }
        return addresses;
    },

    // Xóa một địa chỉ (dựa trên index)
    deleteAddress(index) {
        const addresses = this.getAddresses();
        if (index >= 0 && index < addresses.length) {
            addresses.splice(index, 1);
            localStorage.setItem(ADDRESS_STORAGE_KEY, JSON.stringify(addresses));
        }
        return addresses;
    },

    // Lấy địa chỉ mặc định (ví dụ: địa chỉ đầu tiên)
    getDefaultAddress() {
        const addresses = this.getAddresses();
        return addresses.length > 0 ? addresses[0] : null;
    }
};

export default AddressService;
