import React from 'react';
import '../styles/big-image.css'; // Đảm bảo import file CSS của bạn

function BigImage() {
    return (
        <section className="big-image" id="home">
            <div className="big-image-content" data-aos="fade-right" data-aos-duration="1500">
                <h2>Nhà Hàng Thái Market</h2>
                <p>Đẳng cấp lên ngôi, niềm vui mong chờ, giây phút mộng mơ</p>
                <button className="btn">
                    <a href="../Booking/booking.html" target="_blank" rel="noopener">
                        Đặt bàn
                    </a>
                </button>
            </div>
        </section>
    );
}

export default BigImage;
