import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate để chuyển hướng
import { useAuth } from "../context/AuthContext"; // Import useAuth từ context
import { signIn } from "../services/signinService"; // Import hàm đăng nhập
import "../styles/sign-in.css";

function SignIn() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const { login } = useAuth(); // Lấy hàm login từ AuthContext
    const navigate = useNavigate(); // Khởi tạo useNavigate để điều hướng

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const data = await signIn(email, password); // Gọi API đăng nhập
            // Giả sử API trả về thông tin user từ backend (dựa trên code login bạn cung cấp trước đó)
            const userData = {
                username: data.user.username, // Lấy username từ response
                email: data.user.email, // Lấy email từ response
            };
            login(userData); // Lưu thông tin người dùng vào AuthContext
            alert("Đăng nhập thành công!");
            navigate("/"); // Chuyển hướng về trang chủ
        } catch (err) {
            setError(err.error || "Đăng nhập thất bại!");
        }
    };

    return (
        <section className="sign-in">
            <h1 className="sign-in-heading">Đăng nhập</h1>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit} className="sign-in-form">
                <label htmlFor="email" className="sign-in-label">Email</label>
                <input
                    id="email"
                    type="email"
                    className="sign-in-input"
                    placeholder="Nhập email của bạn"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <label htmlFor="password" className="sign-in-label">Mật Khẩu</label>
                <input
                    id="password"
                    type="password"
                    className="sign-in-input"
                    placeholder="Nhập mật khẩu của bạn"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                <button type="submit" className="sign-in-submit">Đăng nhập</button>
            </form>

            <p className="sign-in-already">
                <span>Bạn chưa có tài khoản?</span>
                <a href="/sign-up" className="sign-in-login">Đăng ký</a>
            </p>
        </section>
    );
}

export default SignIn;
