import React from 'react';
import { useNavigate } from 'react-router-dom';
import "../styles/RelatedItem.css";

const RelatedItems = ({ relatedProducts, currentProductId, addToCart }) => {
    const navigate = useNavigate();

    return (
        <div className="related-items">
            <h3>S·∫£n ph·∫©m c√πng lo·∫°i</h3>
            <div className="related-list">
                {relatedProducts.length > 0 ? (
                    relatedProducts
                        .filter(product => product._id !== currentProductId)
                        .map(product => {
                            const currentPrice = product.prices?.["1"];
                            const originalPrice = currentPrice / (1 - 0.17); // Gi·∫£m 17%
                            const discountPercentage = 17;

                            return (
                                <div key={product._id} className="related-item">
                                    <div
                                        onClick={() => navigate(`/${product.category}/${product._id}`)}
                                        style={{ cursor: 'pointer' }}
                                    >
                                        <img src={product.image} alt={product.name} className="related-image" />
                                        <h4 className="related-name">{product.name}</h4>
                                        <p className="related-price">
                                            {currentPrice
                                                ? `${currentPrice.toLocaleString()} VND / 1kg`
                                                : "Gi√° ch∆∞a c·∫≠p nh·∫≠t"}
                                            {originalPrice && (
                                                <span className="original-price">
                                                    {' '}{Math.round(originalPrice).toLocaleString()} VND
                                                </span>
                                            )}
                                            {discountPercentage && (
                                                <span className="discount"> -{discountPercentage}%</span>
                                            )}
                                        </p>
                                    </div>
                                    <button
                                        className="btn-add"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            addToCart({ 
                                                productId: product._id,
                                                name: product.name, 
                                                price: currentPrice, 
                                                image: product.image, 
                                                size: "1",
                                                quantity: 1 
                                            });
                                        }}
                                    >
                                        üõí
                                    </button>
                                </div>
                            );
                        })
                ) : (
                    <p>Kh√¥ng c√≥ s·∫£n ph·∫©m li√™n quan.</p>
                )}
            </div>
        </div>
    );
};

export default RelatedItems;
