import React, { useState } from 'react';
import '../styles/cartsidebar.css';

function CartSidebar() {
    const [cartItems, setCartItems] = useState([]);
    const [isOpen, setIsOpen] = useState(false); // Tr·∫°ng th√°i ƒë·ªÉ b·∫≠t/t·∫Øt CartSidebar

    // H√†m th√™m s·∫£n ph·∫©m v√†o gi·ªè h√†ng
    window.addToCart = (name, price, image) => {
        setCartItems(prev => [...prev, { name, price, image }]);
        setIsOpen(true); // T·ª± ƒë·ªông m·ªü CartSidebar khi th√™m s·∫£n ph·∫©m
    };

    // H√†m x√≥a s·∫£n ph·∫©m kh·ªèi gi·ªè h√†ng
    const removeFromCart = (index) => {
        setCartItems(prev => prev.filter((_, i) => i !== index));
    };

    // T√≠nh t·ªïng ti·ªÅn
    const total = cartItems.reduce((sum, item) => sum + item.price, 0);

    // H√†m toggle CartSidebar
    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    return (
        <>
            {/* N√∫t toggle CartSidebar */}
            <div className="cart-toggle-btn" onClick={toggleCart}>
                <i className="fas fa-shopping-cart"></i>
                <span className="cart-count-badge">{cartItems.length}</span>
            </div>

            {/* CartSidebar */}
            <div id="cart-sidebar" className={isOpen ? 'open' : 'closed'}>
                <div className="cart-header">
                    <h2>Gi·ªè h√†ng üõí</h2>
                    <button className="close-btn" onClick={toggleCart}>
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <p id="cart-count">S·ªë l∆∞·ª£ng: {cartItems.length}</p>
                <ul id="cart-items">
                    {cartItems.map((item, index) => (
                        <li key={index}>
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <span>{item.name}</span>
                                <span>{item.price.toLocaleString()} VND</span>
                            </div>
                            <button onClick={() => removeFromCart(index)}>X√≥a</button>
                        </li>
                    ))}
                </ul>
                <p>T·ªïng ti·ªÅn: <span id="cart-total">{total.toLocaleString()} VND</span></p>
                <button id="checkout-btn" onClick={() => window.location.href = 'https://your-payment-link.com'}>
                    Thanh to√°n
                </button>
            </div>
        </>
    );
}

export default CartSidebar;
