// src/services/CartService.js (trích đoạn)
export async function increaseQuantity(userId, productId, size) {
    try {
        if (!userId) throw new Error('Thiếu userId');
        const mappedSize = mapSize(size);
        console.log('Increasing quantity:', { userId, productId, size: mappedSize });

        const response = await fetch(`${BASE_URL}/increase`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ userId, productId, size: mappedSize })
        });

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.message || 'Lỗi khi tăng số lượng');
        }

        return await response.json();
    } catch (error) {
        console.error('Error increasing quantity:', error.message);
        throw error;
    }
}

export async function decreaseQuantity(userId, productId, size) {
    try {
        if (!userId) throw new Error('Thiếu userId');
        const mappedSize = mapSize(size);
        console.log('Decreasing quantity:', { userId, productId, size: mappedSize });

        const response = await fetch(`${BASE_URL}/decrease`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ userId, productId, size: mappedSize })
        });

        if (!response.ok) {
            const errorData = await response.json();
            throw new Error(errorData.message || 'Lỗi khi giảm số lượng');
        }

        return await response.json();
    } catch (error) {
        console.error('Error decreasing quantity:', error.message);
        throw error;
    }
}
