import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import ProductItem from './ProductItem';
import RelatedItems from './RelatedItem';
import Footer from './Footer';
import { useCart } from '../context/CartContext';
import '../styles/ProductDetail.css';
import CartSidebar from './CartSidebar';
import ShippingAddressForm from './ShippingAddressForm';
import AddressService from '../services/AddressService';

const ProductDetail = () => {
    const { category, id } = useParams();
    const { addToCart } = useCart();
    const [product, setProduct] = useState(null);
    const [relatedProducts, setRelatedProducts] = useState([]);
    const [selectedAddress, setSelectedAddress] = useState(null);
    const [savedAddresses, setSavedAddresses] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isAddressTableOpen, setIsAddressTableOpen] = useState(false);
    const [editingAddress, setEditingAddress] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:5000/api/products/${id}`)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => console.error('Lỗi tải sản phẩm:', err));

        fetch(`http://localhost:5000/api/products/list?category=${category}`)
            .then(res => res.json())
            .then(data => setRelatedProducts(data.filter(p => p._id !== id)))
            .catch(err => console.error('Lỗi tải sản phẩm liên quan:', err));
    }, [category, id]);

    useEffect(() => {
        const fetchAddresses = async () => {
            try {
                const addresses = await AddressService.getAddresses();
                setSavedAddresses(addresses);
                if (addresses.length > 0) {
                    setSelectedAddress({
                        fullName: addresses[0].fullName,
                        address: `${addresses[0].detail}, ${addresses[0].ward}, ${addresses[0].district}, ${addresses[0].province}`,
                        phone: addresses[0].phone
                    });
                } else {
                    setSelectedAddress(null);
                }
            } catch (error) {
                console.error('Lỗi khi lấy danh sách địa chỉ:', error);
                setSavedAddresses([]);
                setSelectedAddress(null);
            }
        };
        fetchAddresses();
    }, []);

    const handleAddressSelect = async (address) => {
        setSelectedAddress({
            fullName: address.fullName,
            address: address.address, // Nhận trực tiếp từ ShippingAddressForm
            phone: address.phone
        });
        // Cập nhật lại danh sách địa chỉ sau khi thêm/sửa
        try {
            const updatedAddresses = await AddressService.getAddresses();
            setSavedAddresses(updatedAddresses);
        } catch (error) {
            console.error('Lỗi khi cập nhật danh sách địa chỉ:', error);
        }
        setIsModalOpen(false);
        setIsAddressTableOpen(false);
    };

    const handleSelectAddress = (address) => {
        setSelectedAddress({
            fullName: address.fullName,
            address: `${address.detail}, ${address.ward}, ${address.district}, ${address.province}`,
            phone: address.phone
        });
        setIsAddressTableOpen(false);
    };

    const handleEditAddress = (address) => {
        setEditingAddress(address);
        setIsModalOpen(true);
    };

    const handleDeleteAddress = async (addressId) => {
        try {
            await AddressService.deleteAddress(addressId);
            const updatedAddresses = savedAddresses.filter(addr => addr._id !== addressId);
            setSavedAddresses(updatedAddresses);
            if (addressId === savedAddresses[0]?._id && updatedAddresses.length > 0) {
                setSelectedAddress({
                    fullName: updatedAddresses[0].fullName,
                    address: `${updatedAddresses[0].detail}, ${updatedAddresses[0].ward}, ${updatedAddresses[0].district}, ${updatedAddresses[0].province}`,
                    phone: updatedAddresses[0].phone
                });
            } else if (updatedAddresses.length === 0) {
                setSelectedAddress(null);
            }
        } catch (error) {
            console.error('Lỗi khi xóa địa chỉ:', error);
        }
    };

    const openModal = () => {
        setEditingAddress(null);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingAddress(null);
    };

    if (!product) {
        return <div>Đang tải sản phẩm...</div>;
    }

    return (
        <div className="product-detail">
            <Header />
            <div className="product-content">
                <ProductItem
                    product={product}
                    addToCart={addToCart}
                    selectedAddress={selectedAddress} // Truyền selectedAddress xuống ProductItem
                />
                {/* Hiển thị địa chỉ đã chọn và nút mở bảng địa chỉ */}
                <div className="address-section">
                    <h3>Địa chỉ giao hàng</h3>
                    {selectedAddress ? (
                        <div className="selected-address-section">
                            <p><strong>Số điện thoại:</strong> {selectedAddress.phone}</p>
                            <p><strong>Địa chỉ:</strong> {selectedAddress.address}</p>
                        </div>
                    ) : (
                        <p>Chưa có địa chỉ giao hàng</p>
                    )}
                    <button
                        onClick={() => setIsAddressTableOpen(!isAddressTableOpen)}
                        className="change-address-btn"
                    >
                        {selectedAddress ? 'Thay đổi địa chỉ' : 'Chọn địa chỉ giao hàng'}
                    </button>

                    {/* Bảng hiển thị địa chỉ nổi */}
                    {isAddressTableOpen && (
                        <div className="address-table-container">
                            <table className="address-table">
                                <thead>
                                    <tr>
                                        <th>Họ và tên</th>
                                        <th>Địa chỉ</th>
                                        <th>Số điện thoại</th>
                                        <th>Hành động</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {savedAddresses.length > 0 ? (
                                        savedAddresses.map((address) => (
                                            <tr key={address._id}>
                                                <td>{address.fullName}</td>
                                                <td>{`${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</td>
                                                <td>{address.phone}</td>
                                                <td>
                                                    <button
                                                        onClick={() => handleSelectAddress(address)}
                                                        className="select-btn"
                                                    >
                                                        Chọn
                                                    </button>
                                                    <button
                                                        onClick={() => handleEditAddress(address)}
                                                        className="edit-btn"
                                                    >
                                                        Sửa
                                                    </button>
                                                    <button
                                                        onClick={() => handleDeleteAddress(address._id)}
                                                        className="delete-btn"
                                                    >
                                                        Xóa
                                                    </button>
                                                </td>
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td colSpan="4">Chưa có địa chỉ nào</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                            <button onClick={openModal} className="add-address-btn">
                                Thêm địa chỉ mới
                            </button>
                        </div>
                    )}
                </div>

                {/* Modal hiển thị form thêm/sửa địa chỉ */}
                {isModalOpen && (
                    <div className="modal-overlay">
                        <div className="modal-content">
                            <button
                                onClick={closeModal}
                                className="modal-close-btn"
                            >
                                ×
                            </button>
                            <h2>{editingAddress ? 'Sửa địa chỉ' : 'Thêm địa chỉ mới'}</h2>
                            <ShippingAddressForm
                                onAddressSelect={handleAddressSelect}
                                onClose={closeModal}
                                initialData={editingAddress}
                            />
                        </div>
                    </div>
                )}

                <RelatedItems
                    relatedProducts={relatedProducts}
                    currentProductId={id}
                    addToCart={addToCart}
                    selectedAddress={selectedAddress}
                />
            </div>
            <Footer />
            <CartSidebar />
        </div>
    );
};

export default ProductDetail;
