import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/Payment.css"; // File CSS để tùy chỉnh giao diện (nếu cần)

const Payment = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Kiểm tra dữ liệu từ state
  useEffect(() => {
    if (!state || !state.selectedItems || !state.total || state.total <= 0) {
      setError("Không có sản phẩm nào để thanh toán. Vui lòng quay lại giỏ hàng.");
      setTimeout(() => navigate("/cart"), 3000); // Chuyển hướng về giỏ hàng sau 3 giây
    }
  }, [state, navigate]);

  // Tạo orderInfo từ selectedItems
  const createOrderInfo = (items) => {
    return items
      .map((item) => `${item.name} ${item.size} - ${item.quantity} x ${item.price}`)
      .join(", ");
  };

  const handlePayment = async () => {
    if (!state || !state.selectedItems || !state.total) {
      setError("Dữ liệu thanh toán không hợp lệ.");
      return;
    }

    const { selectedItems, total, selectedAddress } = state;
    const orderInfo = createOrderInfo(selectedItems);
    const amount = total;

    setLoading(true);
    setError(null);

    try {
      // Gửi request đến VNPay Server (cổng 5001)
      const response = await axios.post("http://localhost:5001/create_payment", {
        amount: amount, // Tổng tiền
        orderInfo: orderInfo, // Mô tả đơn hàng
        items: selectedItems, // Danh sách sản phẩm (có thể dùng để lưu đơn hàng sau này)
        address: selectedAddress, // Địa chỉ giao hàng (có thể dùng để lưu đơn hàng sau này)
      });

      if (response.data.status === "success") {
        // Chuyển hướng người dùng đến Payment URL
        window.location.href = response.data.url;
      } else {
        setError("Không thể tạo URL thanh toán. Vui lòng thử lại.");
      }
    } catch (error) {
      console.error("Lỗi khi tạo URL thanh toán:", error);
      setError("Đã có lỗi xảy ra. Vui lòng thử lại!");
    } finally {
      setLoading(false);
    }
  };

  if (!state || !state.selectedItems || !state.total) {
    return (
      <div className="payment-container">
        <h2>Thanh toán</h2>
        <p className="error-message">{error || "Đang tải..."}</p>
      </div>
    );
  }

  const { selectedItems, total, selectedAddress } = state;

  return (
    <div className="payment-container">
      <h2>Thanh toán</h2>

      {/* Hiển thị thông tin đơn hàng */}
      <div className="order-summary">
        <h3>Thông tin đơn hàng</h3>
        <div className="order-items">
          {selectedItems.map((item) => (
            <div key={`${item.productId}-${item.size}`} className="order-item">
              <img src={item.image} alt={item.name} className="order-item-image" />
              <div className="order-item-details">
                <p>
                  <strong>{item.name}</strong>
                </p>
                <p>Phân loại: {item.color || item.category || "Không xác định"}, {item.size}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Đơn giá: {item.price.toLocaleString()}đ</p>
                <p>Tổng: {(item.price * item.quantity).toLocaleString()}đ</p>
              </div>
            </div>
          ))}
        </div>
        <div className="order-total">
          <p>
            Tổng thanh toán ({selectedItems.length} sản phẩm):{" "}
            <strong>{total.toLocaleString()}đ</strong>
          </p>
        </div>
      </div>

      {/* Hiển thị địa chỉ giao hàng */}
      <div className="shipping-address">
        <h3>Địa chỉ giao hàng</h3>
        {selectedAddress ? (
          <div>
            <p>
              <strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})
            </p>
            <p>{selectedAddress.address}</p>
          </div>
        ) : (
          <p>Chưa có địa chỉ giao hàng.</p>
        )}
      </div>

      {/* Nút thanh toán */}
      <div className="payment-actions">
        {error && <p className="error-message">{error}</p>}
        <button
          onClick={handlePayment}
          disabled={loading || !selectedAddress}
          className="payment-btn"
        >
          {loading ? "Đang xử lý..." : "Thanh toán với VNPay"}
        </button>
      </div>
    </div>
  );
};

export default Payment;
