// components/Profile.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Sử dụng useAuth
import axios from 'axios';

const Profile = () => {
  const { user } = useAuth(); // Sử dụng useAuth để lấy user
  const [newPassword, setNewPassword] = useState('');
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [rating, setRating] = useState({});
  const [review, setReview] = useState({});

  // Lấy danh sách đơn hàng của user khi component mount
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: { Authorization: `Bearer ${user.token}` },
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Lỗi khi lấy đơn hàng:', error);
      }
    };
    if (user && user.token) fetchOrders();
  }, [user]);

  // Gửi đánh giá cho sản phẩm
  const handleSubmitReview = async (orderId, productId) => {
    try {
      const response = await axios.post(
        'http://localhost:5000/api/reviews',
        {
          orderId,
          productId,
          rating: rating[productId] || 0,
          comment: review[productId] || '',
        },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      console.log('Đánh giá đã gửi:', response.data);
      setRating((prev) => ({ ...prev, [productId]: 0 }));
      setReview((prev) => ({ ...prev, [productId]: '' }));
    } catch (error) {
      console.error('Lỗi khi gửi đánh giá:', error);
    }
  };

  // Xử lý thay đổi mật khẩu
  const handlePasswordChange = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        'http://localhost:5000/api/users/change-password',
        { newPassword },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      console.log('Mật khẩu đã được cập nhật:', response.data);
      setNewPassword('');
    } catch (error) {
      console.error('Lỗi khi đổi mật khẩu:', error.response?.data?.message || error.message);
    }
  };

  // Mã hóa email bằng dấu ****
  const maskEmail = (email) => {
    const [name, domain] = email.split('@');
    return `${name.substring(0, 2)}****@${domain}`;
  };

  return (
    <div className="profile-container">
      {/* Thông tin người dùng */}
      <div className="user-info">
        <h2>Thông tin cá nhân</h2>
        <p>Email: {user?.email ? maskEmail(user.email) : 'Chưa đăng nhập'}</p>
        
        <form onSubmit={handlePasswordChange}>
          <h3>Đổi mật khẩu</h3>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="Mật khẩu mới"
          />
          <button type="submit">Cập nhật mật khẩu</button>
        </form>
      </div>

      {/* Lịch sử đơn hàng */}
      <div className="order-history">
        <h2>Lịch sử đơn hàng</h2>
        {orders.length > 0 ? (
          <ul>
            {orders.map((order) => (
              <li
                key={order._id}
                onClick={() => setSelectedOrder(order)}
                className="order-item"
              >
                <p>Mã đơn: {order._id}</p>
                <p>Ngày: {new Date(order.createdAt).toLocaleDateString()}</p>
                <p>Trạng thái: {order.status}</p>
                <p>Tổng tiền: {order.totalPrice.toLocaleString()} VND</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Chưa có đơn hàng nào</p>
        )}
      </div>

      {/* Chi tiết đơn hàng và đánh giá */}
      {selectedOrder && (
        <div className="order-details">
          <h3>Chi tiết đơn hàng {selectedOrder._id}</h3>
          <p>Trạng thái: {selectedOrder.status}</p>
          <p>Địa chỉ: {selectedOrder.address.fullName}, {selectedOrder.address.address}</p>
          <p>Số điện thoại: {selectedOrder.address.phone}</p>
          
          <div className="products-list">
            <h4>Sản phẩm trong đơn hàng</h4>
            {selectedOrder.products.map((item) => (
              <div key={item.productId._id} className="product-item">
                <img src={item.productId.image} alt={item.productId.name} width="50" />
                <div>
                  <p>Tên: {item.productId.name}</p>
                  <p>Số lượng: {item.quantity}</p>
                  <p>Giá: {item.price.toLocaleString()} VND</p>
                  {item.size && <p>Kích thước: {item.size}</p>}
                  {item.color && <p>Màu: {item.color}</p>}
                </div>

                {/* Form đánh giá nếu trạng thái là Confirmed */}
                {selectedOrder.status === 'Confirmed' && (
                  <div className="review-section">
                    <h5>Đánh giá sản phẩm</h5>
                    <div className="rating">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <span
                          key={star}
                          onClick={() =>
                            setRating((prev) => ({ ...prev, [item.productId._id]: star }))
                          }
                          style={{
                            cursor: 'pointer',
                            color: star <= (rating[item.productId._id] || 0) ? '#ffd700' : '#ccc',
                          }}
                        >
                          ★
                        </span>
                      ))}
                    </div>
                    <textarea
                      value={review[item.productId._id] || ''}
                      onChange={(e) =>
                        setReview((prev) => ({ ...prev, [item.productId._id]: e.target.value }))
                      }
                      placeholder="Viết nhận xét của bạn..."
                    />
                    <button
                      onClick={() => handleSubmitReview(selectedOrder._id, item.productId._id)}
                    >
                      Gửi đánh giá
                    </button>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Profile;
