import React, { useEffect } from 'react';
import '../styles/cartsidebar.css';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

function CartSidebar() {
    const {
        cartItems,
        removeFromCart,
        isOpen,
        toggleCart,
        total,
        totalItems,
        increaseQuantity,
        decreaseQuantity,
        toggleItemSelection,
        selectAll,
        fetchCartFromServer,
        loadCartFromLocalStorage
    } = useCart();

    const { user } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (user && user._id) {
            fetchCartFromServer(user._id);
        } else {
            loadCartFromLocalStorage();
        }
    }, [user, fetchCartFromServer, loadCartFromLocalStorage]);

    const hasSelectedItems = cartItems.some(item => item.selected);
    const allSelected = cartItems.length > 0 && cartItems.every(item => item.selected);

    const handleToggleAll = () => {
        selectAll(!allSelected);
    };

    const handleCheckout = () => {
        if (hasSelectedItems) {
            // Chuyển hướng trực tiếp đến endpoint create_payment với tổng tiền
            window.location.href = `http://localhost:5000/create_payment?amount=${total}`;
        } else {
            alert('Vui lòng chọn ít nhất một sản phẩm để thanh toán!');
        }
    };

    return (
        <>
            <div className="cart-toggle-btn" onClick={toggleCart}>
                <i className="fas fa-shopping-cart"></i>
                <span className="cart-count-badge">{totalItems}</span>
            </div>

            <div id="cart-sidebar" className={isOpen ? 'open' : 'closed'}>
                <div className="cart-header">
                    <h2>Giỏ hàng 🛒</h2>
                    <button className="close-btn" onClick={toggleCart}>
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <div className="cart-actions">
                    {cartItems.length > 0 && (
                        <button
                            className={`toggle-all-btn ${allSelected ? 'checked' : ''}`}
                            onClick={handleToggleAll}
                        >
                            {allSelected && <i className="fas fa-check"></i>}
                        </button>
                    )}
                    <p id="cart-count">Tổng số lượng: {totalItems}</p>
                </div>
                <ul id="cart-items">
                    {cartItems.map((item, index) => (
                        <li key={index} className="cart-item">
                            <input
                                type="checkbox"
                                checked={item.selected || false}
                                onChange={() => toggleItemSelection(item.productId, item.size)}
                                className="cart-item-checkbox"
                            />
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <span>
                                    {typeof item.name === 'string' && typeof item.size === 'string' 
                                        ? `${item.name} (${item.size}kg)`
                                        : "Tên sản phẩm không hợp lệ"}
                                </span>
                                <span>
                                    {typeof item.price === 'number' 
                                        ? `${item.price.toLocaleString()} VND` 
                                        : "Chưa có giá"}
                                </span>
                                <div className="quantity-controls">
                                    <button onClick={() => decreaseQuantity(item.productId, item.size)}>-</button>
                                    <span>{typeof item.quantity === 'number' ? item.quantity : 1}</span>
                                    <button onClick={() => increaseQuantity(item.productId, item.size)}>+</button>
                                </div>
                            </div>
                            <button onClick={() => removeFromCart(item.productId, item.size)}>Xóa</button>
                        </li>
                    ))}
                </ul>
                <div className="cart-footer">
                    <p>
                        Tổng tiền (đã chọn): 
                        <span id="cart-total">
                            {typeof total === 'number' ? `${total.toLocaleString()} VND` : "Không xác định"}
                        </span>
                    </p>
                    <button id="checkout-btn" onClick={handleCheckout}>
                        Thanh toán
                    </button>
                </div>
            </div>
        </>
    );
}

export default CartSidebar;
