import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Payment.css';

function Payment() {
    const { cartItems } = useCart();
    const location = useLocation();
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(false);

    const selectedItems = location.state?.item 
        ? [location.state.item]
        : cartItems.filter(item => item.selected);

    const paymentTotal = selectedItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

    useEffect(() => {
        if (selectedItems.length === 0) {
            navigate('/cart');
        }
    }, [selectedItems, navigate]);

    const handlePayment = async () => {
        if (selectedItems.length === 0 || paymentTotal <= 0) {
            alert('Không có sản phẩm nào để thanh toán!');
            navigate('/cart');
            return;
        }

        setIsLoading(true);
        try {
            const orderInfo = selectedItems.map(item => `${item.name} (${item.size}kg) - ${item.quantity} x ${item.price.toLocaleString()} VND`).join('; ');
            const encodedOrderInfo = encodeURIComponent(orderInfo);

            const response = await fetch(`http://localhost:5000/create_payment?amount=${paymentTotal}&orderInfo=${encodedOrderInfo}`);
            
            const responseText = await response.text();
            console.log("Response từ server:", responseText);
            
            let data;
            try {
                data = JSON.parse(responseText);
            } catch (err) {
                throw new Error("Phản hồi không phải JSON hợp lệ: " + responseText);
            }
            
            if (data.status === 'success' && data.url) {
                window.location.href = data.url;
            } else {
                throw new Error('Không nhận được URL thanh toán hợp lệ.');
            }
        } catch (error) {
            console.error('Lỗi khi tạo thanh toán:', error);
            alert(`Có lỗi xảy ra: ${error.message}`);
            setIsLoading(false);
        }
    };

    return (
        <div className="payment-container">
            <h2>Thanh toán</h2>
            {selectedItems.length > 0 ? (
                <>
                    <div className="payment-items">
                        {selectedItems.map((item, index) => (
                            <div key={index} className="payment-item">
                                <img src={item.image} alt={item.name} className="payment-item-image" />
                                <div className="payment-item-details">
                                    <p>{item.name} ({item.size}kg)</p>
                                    <p>Đơn giá: {item.price.toLocaleString()} VND</p>
                                    <p>Số lượng: {item.quantity}</p>
                                    <p>Tổng: {(item.price * item.quantity).toLocaleString()} VND</p>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="payment-summary">
                        <p>Tổng cộng ({selectedItems.length} sản phẩm): <strong>{paymentTotal.toLocaleString()} VND</strong></p>
                        <button 
                            className="payment-btn" 
                            onClick={handlePayment}
                            disabled={isLoading}
                        >
                            {isLoading ? 'Đang xử lý...' : 'Thanh toán bằng VNPay'}
                        </button>
                    </div>
                </>
            ) : (
                <p>Không có sản phẩm nào để thanh toán.</p>
            )}
        </div>
    );
}

export default Payment;
