import React, { useState } from "react";
import axios from "axios";

const Payment = () => {
  const [amount, setAmount] = useState("");
  const [orderInfo, setOrderInfo] = useState("");

  const handlePayment = async () => {
    try {
      const response = await axios.post("http://localhost:5001/create_payment", {
        amount: amount,
        orderInfo: orderInfo || "Dau Tay Say 0.5kg - 1 x 130000 VND",
      });

      if (response.data.status === "success") {
        window.location.href = response.data.url;
      } else {
        alert("Không thể tạo URL thanh toán!");
      }
    } catch (error) {
      console.error("Lỗi khi tạo URL thanh toán:", error);
      alert("Đã có lỗi xảy ra. Vui lòng thử lại!");
    }
  };

  return (
    <div>
      <h2>Thanh toán với VNPay</h2>
      <div>
        <label>Số tiền (VND):</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Nhập số tiền"
        />
      </div>
      <div>
        <label>Mô tả đơn hàng:</label>
        <input
          type="text"
          value={orderInfo}
          onChange={(e) => setOrderInfo(e.target.value)}
          placeholder="Mô tả đơn hàng"
        />
      </div>
      <button onClick={handlePayment}>Thanh toán</button>
    </div>
  );
};

export default Payment;
