import React from 'react';
import '../styles/cartsidebar.css'; // ƒê·∫£m b·∫£o import file CSS c·ªßa b·∫°n

function CartSidebar({ cartItems, removeFromCart }) {
 
    // T√≠nh t·ªïng ti·ªÅn
    const totalPrice = cartItems.reduce((total, item) => total + item.price, 0);

    return (
        <div id="cart-sidebar">
            <h2>Gi·ªè h√†ng üõí</h2>
            {/* Hi·ªÉn th·ªã t·ªïng s·ªë l∆∞·ª£ng s·∫£n ph·∫©m */}
            <p id="cart-count">S·ªë l∆∞·ª£ng: {totalItems}</p>
            <ul id="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index}>
                        <span>
                            {item.name} - {item.price.toLocaleString()} VND
                        </span>
                        <button onClick={() => removeFromCart(index)}>X√≥a</button>
                    </li>
                ))}
            </ul>
            <p>
                T·ªïng ti·ªÅn: <span id="cart-total">{totalPrice.toLocaleString()} VND</span>
            </p>
            {/* N√∫t thanh to√°n */}
            <button
                id="checkout-btn"
                onClick={() => (window.location.href = 'https://your-payment-link.com')}
            >
                Thanh to√°n
            </button>
        </div>
    );
}

export default CartSidebar;
