import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [user, setUser] = useState(null); // Giả sử user được set từ AuthContext

    useEffect(() => {
        if (user) {
            fetchCartFromServer(user.id);
        } else {
            loadCartFromLocalStorage();
        }
    }, [user]);

    useEffect(() => {
        if (!user) {
            saveCartToLocalStorage();
        }
    }, [cartItems, user]);

    const toggleCart = () => setIsOpen(prev => !prev);

    const loadCartFromLocalStorage = () => {
        try {
            const savedCart = localStorage.getItem('cartItems');
            setCartItems(savedCart ? JSON.parse(savedCart) : []);
        } catch (error) {
            console.error("Lỗi khi lấy giỏ hàng từ localStorage:", error);
            setCartItems([]);
        }
    };

    const saveCartToLocalStorage = () => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    };

    const fetchCartFromServer = async (userId) => {
        try {
            const res = await fetch(`http://localhost:5000/api/carts/${userId}`);
            const data = await res.json();
            setCartItems(data.items || []);
        } catch (error) {
            console.error("Lỗi khi lấy giỏ hàng từ server:", error);
        }
    };

    const addToCart = async (product) => {
        const { productId, quantity = 1, size, name, price, image } = product;

        if (user) {
            try {
                // Gửi request để thêm trực tiếp vào database
                const res = await fetch('http://localhost:5000/api/carts/add', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ userId: user.id, productId, quantity, size })
                });

                if (!res.ok) {
                    throw new Error('Lỗi khi thêm vào giỏ hàng');
                }

                // Lấy lại giỏ hàng từ server để đồng bộ state
                await fetchCartFromServer(user.id);
            } catch (error) {
                console.error("Lỗi khi thêm vào giỏ hàng:", error);
            }
        } else {
            // Nếu chưa đăng nhập, chỉ cập nhật localStorage
            setCartItems(prev => {
                const existingItem = prev.find(item => item.productId === productId && item.size === size);
                if (existingItem) {
                    return prev.map(item =>
                        item.productId === productId && item.size === size
                            ? { ...item, quantity: item.quantity + quantity }
                            : item
                    );
                }
                return [...prev, { productId, quantity, size, name, price, image, selected: false }];
            });
        }
    };

    const increaseQuantity = async (productId, size) => {
        if (user) {
            const res = await fetch('http://localhost:5000/api/carts/increase', {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userId: user.id, productId, size })
            });
            const updatedCart = await res.json();
            setCartItems(updatedCart.items);
        } else {
            setCartItems(prev => prev.map(item =>
                item.productId === productId && item.size === size
                    ? { ...item, quantity: item.quantity + 1 }
                    : item
            ));
        }
    };

    const decreaseQuantity = async (productId, size) => {
        if (user) {
            const res = await fetch('http://localhost:5000/api/carts/decrease', {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userId: user.id, productId, size })
            });
            const updatedCart = await res.json();
            setCartItems(updatedCart.items);
        } else {
            setCartItems(prev => prev.map(item =>
                item.productId === productId && item.size === size && item.quantity > 1
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            ).filter(item => item.quantity > 0));
        }
    };

    const removeFromCart = async (productId, size) => {
        if (user) {
            const res = await fetch('http://localhost:5000/api/carts/remove', {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userId: user.id, productId, size })
            });
            const updatedCart = await res.json();
            setCartItems(updatedCart.items);
        } else {
            setCartItems(prev => prev.filter(item =>
                !(item.productId === productId && item.size === size)
            ));
        }
    };

    const toggleItemSelection = (index) => {
        setCartItems(prev => prev.map((item, i) =>
            i === index ? { ...item, selected: !item.selected } : item
        ));
    };

    const selectAll = () => {
        setCartItems(prev => prev.map(item => ({ ...item, selected: true })));
    };

    const deselectAll = () => {
        setCartItems(prev => prev.map(item => ({ ...item, selected: false })));
    };

    const total = cartItems
        .filter(item => item.selected)
        .reduce((sum, item) => sum + (item.price * item.quantity), 0);

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);

    return (
        <CartContext.Provider
            value={{
                cartItems,
                setCartItems,
                isOpen,
                toggleCart,
                total,
                totalItems,
                addToCart,
                increaseQuantity,
                decreaseQuantity,
                removeFromCart,
                toggleItemSelection,
                selectAll,
                deselectAll,
                fetchCartFromServer,
                loadCartFromLocalStorage
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error("useCart must be used within a CartProvider");
    }
    return context;
};
