import React from 'react';
import '../styles/team.css'; // Đảm bảo import file CSS của bạn

function Team() {
    // Dữ liệu đội ngũ đầu bếp
    const teamMembers = [
        {
            name: 'Mai Thái Huy',
            role: 'Bếp trưởng',
            image: '../images/daubep1.jpg',
        },
        {
            name: 'Lưu Đức Quang',
            role: 'Bếp phó',
            image: '../images/daubep2.jpg',
        },
        {
            name: 'Ma Thế Ngọc',
            role: 'Phụ bếp',
            image: '../images/daubep3.jpg',
        },
    ];

    return (
        <section className="team section-padding" id="employee">
            <div className="container">
                <div className="row">
                    <div className="section-title" data-aos="fade-up-right" data-aos-duration="1500">
                        <h2 data-title="Cộng sự">Đầu bếp tiêu biểu của chúng tôi</h2>
                    </div>
                </div>
                <div className="row">
                    {teamMembers.map((member, index) => (
                        <div className="team-items" data-aos="zoom-in-up" data-aos-duration="1500" key={index}>
                            <img src={member.image} alt={member.name} />
                            <div className="team-items-text">
                                <h2>{member.name}</h2>
                                <span>{member.role}</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Team;
