import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import ProductItem from './ProductItem';
import RelatedItems from './RelatedItem';
import Footer from './Footer';
import { useCart } from '../context/CartContext';
import '../styles/ProductDetail.css';
import CartSidebar from './CartSidebar';
import ShippingAddressForm from './ShippingAddressForm';
import AddressService from '../services/AddressService';

const ProductDetail = () => {
    const { category, id } = useParams();
    const { addToCart } = useCart();
    const [product, setProduct] = useState(null);
    const [relatedProducts, setRelatedProducts] = useState([]);
    const [selectedAddress, setSelectedAddress] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false); // State để điều khiển modal

    useEffect(() => {
        fetch(`http://localhost:5000/api/products/${id}`)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => console.error('Lỗi tải sản phẩm:', err));

        fetch(`http://localhost:5000/api/products/list?category=${category}`)
            .then(res => res.json())
            .then(data => setRelatedProducts(data.filter(p => p._id !== id)))
            .catch(err => console.error('Lỗi tải sản phẩm liên quan:', err));
    }, [category, id]);

    useEffect(() => {
        const defaultAddress = AddressService.getDefaultAddress();
        setSelectedAddress(defaultAddress);
    }, []);

    const handleAddressSelect = (address) => {
        setSelectedAddress(address);
        setIsModalOpen(false); // Đóng modal sau khi chọn địa chỉ
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    if (!product) {
        return <div>Đang tải sản phẩm...</div>;
    }

    return (
        <div className="product-detail">
            <Header />
            <div className="product-content">
                <ProductItem product={product} addToCart={addToCart} />
                <RelatedItems relatedProducts={relatedProducts} currentProductId={id} addToCart={addToCart} />
                
                {/* Hiển thị địa chỉ đã chọn và nút mở modal */}
                <div className="address-section">
                    <h3>Địa chỉ giao hàng</h3>
                    {selectedAddress ? (
                        <div className="selected-address-section">
                            <p><strong>{selectedAddress.fullName}</strong></p>
                            <p>{selectedAddress.address}</p>
                            <p>Số điện thoại: {selectedAddress.phone}</p>
                        </div>
                    ) : (
                        <p>Chưa có địa chỉ giao hàng</p>
                    )}
                    <button
                        onClick={openModal}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: '#4CAF50',
                            color: 'white',
                            border: 'none',
                            cursor: 'pointer',
                            marginTop: '10px'
                        }}
                    >
                        {selectedAddress ? 'Thay đổi địa chỉ' : 'Chọn địa chỉ giao hàng'}
                    </button>
                </div>

                {/* Modal hiển thị form chọn địa chỉ */}
                {isModalOpen && (
                    <div className="modal-overlay">
                        <div className="modal-content">
                            <button
                                onClick={closeModal}
                                style={{
                                    position: 'absolute',
                                    top: '10px',
                                    right: '10px',
                                    background: 'none',
                                    border: 'none',
                                    fontSize: '24px',
                                    cursor: 'pointer'
                                }}
                            >
                                &times;
                            </button>
                            <h2>Chọn địa chỉ nhận hàng</h2>
                            <ShippingAddressForm onAddressSelect={handleAddressSelect} />
                        </div>
                    </div>
                )}
            </div>
            <Footer />
            <CartSidebar />
        </div>
    );
};

export default ProductDetail;
