import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import BigImage from './components/BigImage';
import About from './components/About';
import Menu from './components/Menu';
import CartSidebar from './components/CartSidebar';
import Team from './components/Team';
import SignIn from './components/SignIn'; // Import trang đăng nhập

function App() {
    // State để quản lý giỏ hàng
    const [cartItems, setCartItems] = useState([]);

    // Hàm thêm vào giỏ hàng
    const addToCart = (name, price, image) => {
        const newItem = { name, price, image };
        setCartItems([...cartItems, newItem]);
    };

    // Hàm xóa món khỏi giỏ hàng
    const removeFromCart = (index) => {
        const updatedCart = cartItems.filter((_, i) => i !== index);
        setCartItems(updatedCart);
    };

    return (
        <Router>
            <Header /> {/* Header luôn hiển thị ở mọi trang */}
            <Routes>
                <Route 
                    path="/" 
                    element={
                        <>
                            <BigImage />
                            <About />
                            <Menu addToCart={addToCart} />
                            <CartSidebar cartItems={cartItems} removeFromCart={removeFromCart} />
                            <Team />
                        </>
                    } 
                />
                <Route path="/sign-in" element={<SignIn />} /> {/* Thêm trang đăng nhập */}
            </Routes>
        </Router>
    );
}

export default App;
