import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import "../styles/Payment.css";

const Payment = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!state || (!state.selectedItems && !state.item) || !state.total || state.total <= 0) {
      setError("Không có sản phẩm nào để thanh toán. Vui lòng quay lại giỏ hàng.");
      setTimeout(() => navigate("/cart"), 3000);
      return;
    }
  }, [state, navigate]);

  const items = state.selectedItems || (state.item ? [state.item] : []);
  const total = state.total;
  const selectedAddress = state.selectedAddress || {};

  const createOrderInfo = (items) => {
    return items
      .map((item) => `${item.name} ${item.size} - ${item.quantity} x ${item.price}`)
      .join(", ");
  };

  const handlePayment = async () => {
    if (!items.length || !total) {
      setError("Dữ liệu thanh toán không hợp lệ.");
      return;
    }
    if (!selectedAddress || !selectedAddress.fullName || !selectedAddress.phone || !selectedAddress.address) {
      setError("Vui lòng thêm đầy đủ thông tin địa chỉ giao hàng trước khi thanh toán.");
      setTimeout(() => navigate("/cart"), 3000);
      return;
    }

    // Lấy userId từ user (nếu có) hoặc localStorage (nếu không đăng nhập)
    const userId = user?._id || localStorage.getItem("tempUserId") || "guest";
    if (!userId) {
      setError("Không tìm thấy userId. Vui lòng thử lại hoặc đăng nhập.");
      return;
    }

    const orderInfo = createOrderInfo(items);
    const amount = total;

    setLoading(true);
    setError(null);

    try {
      console.log("Gửi yêu cầu tạo thanh toán:", {
        userId,
        amount,
        orderInfo,
        items,
        address: selectedAddress,
      });
      const response = await axios.post(
        "http://localhost:5001/create_payment",
        {
          userId,
          amount,
          orderInfo,
          items,
          address: selectedAddress,
        }
        // Không gửi header Authorization vì backend không yêu cầu token
      );
      console.log("Phản hồi từ server:", response.data);
      if (response.data.status === "success") {
        localStorage.setItem(
          "pendingOrder",
          JSON.stringify({
            userId,
            items,
            total: amount,
            address: selectedAddress,
            orderInfo,
          })
        );
        window.location.href = response.data.url;
      } else {
        setError(response.data.error || "Không thể tạo URL thanh toán. Vui lòng thử lại.");
      }
    } catch (error) {
      console.error("Lỗi khi tạo URL thanh toán:", error.response ? error.response.data : error.message);
      setError(
        "Đã có lỗi xảy ra. Vui lòng thử lại! Chi tiết: " +
          (error.response ? error.response.data.error : error.message)
      );
    } finally {
      setLoading(false);
    }
  };

  if (!items.length || !total) {
    return (
      <div className="payment-container">
        <h2>Thanh toán</h2>
        <p className="error-message">{error || "Đang tải..."}</p>
      </div>
    );
  }

  return (
    <div className="payment-container with-background">
      <h2>Thanh toán</h2>

      <div className="order-summary">
        <h3>Thông tin đơn hàng</h3>
        <div className="order-items">
          {items.map((item) => (
            <div key={`${item.productId}-${item.size}`} className="order-item">
              <img src={item.image} alt={item.name} className="order-item-image" />
              <div className="order-item-details">
                <p>
                  <strong>{item.name}</strong>
                </p>
                <p>Phân loại: {item.size}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Đơn giá: {item.price.toLocaleString()}đ</p>
                <p>Tổng: {(item.price * item.quantity).toLocaleString()}đ</p>
              </div>
            </div>
          ))}
        </div>
        <div className="order-total">
          <p>
            Tổng thanh toán ({items.length} sản phẩm): <strong>{total.toLocaleString()}đ</strong>
          </p>
        </div>
      </div>

      <div className="shipping-address">
        <h3>Địa chỉ giao hàng</h3>
        {selectedAddress.fullName && selectedAddress.phone && selectedAddress.address ? (
          <div>
            <p>
              <strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})
            </p>
            <p>{selectedAddress.address}</p>
            {selectedAddress.email && <p>Email: {selectedAddress.email}</p>}
            {selectedAddress.note && <p>Ghi chú: {selectedAddress.note}</p>}
          </div>
        ) : (
          <p>Chưa có địa chỉ giao hàng đầy đủ. Vui lòng thêm địa chỉ trong giỏ hàng.</p>
        )}
      </div>

      <div className="payment-actions">
        {error && <p className="error-message">{error}</p>}
        <button
          onClick={handlePayment}
          disabled={
            loading ||
            !selectedAddress ||
            !selectedAddress.fullName ||
            !selectedAddress.phone ||
            !selectedAddress.address
          }
          className="payment-btn"
        >
          {loading ? "Đang xử lý..." : "Thanh toán với VNPay"}
        </button>
      </div>
    </div>
  );
};

export default Payment;
