// src/components/RelatedItems.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AddressService from '../services/AddressService';
import ShippingAddressForm from './ShippingAddressForm';
import "../styles/RelatedItems.css";

// Hàm ánh xạ size từ giao diện sang database
const mapSizeToDatabase = (size) => {
  switch (size) {
    case '0.25': return '250';
    case '0.5': return '500';
    case '1': return '1000';
    default: return size;
  }
};

// Hàm ánh xạ size để hiển thị
const mapSizeToDisplay = (size) => {
  switch (size) {
    case '0.25': return '0.25kg';
    case '0.5': return '0.5kg';
    case '1': return '1kg';
    default: return `${size}kg`;
  }
};

const RelatedItems = ({ relatedProducts, currentProductId, addToCart, selectedAddress: parentSelectedAddress }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [addresses, setAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(parentSelectedAddress || null);
  const [isAddressModalOpen, setIsAddressModalOpen] = useState(false);
  const [isAddressFormOpen, setIsAddressFormOpen] = useState(false);
  const [editingAddress, setEditingAddress] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [pendingProduct, setPendingProduct] = useState(null);

  console.log('Related products in RelatedItems:', relatedProducts);

  useEffect(() => {
    const fetchAddresses = async () => {
      if (!user?.token) {
        setError('Vui lòng đăng nhập để quản lý địa chỉ.');
        return;
      }

      setLoading(true);
      try {
        const token = user.token;
        console.log('Token gửi đi trong fetchAddresses (RelatedItems):', token);
        const addressList = await AddressService.getAddresses(token);
        setAddresses(addressList || []);
        if (!parentSelectedAddress && addressList.length > 0) {
          const defaultAddress = addressList[0];
          setSelectedAddress({
            fullName: defaultAddress.fullName,
            phone: defaultAddress.phone,
            address: `${defaultAddress.detail}, ${defaultAddress.ward}, ${defaultAddress.district}, ${defaultAddress.province}`,
          });
        }
      } catch (error) {
        console.error('Lỗi khi lấy địa chỉ:', error);
        setError(error.message || 'Không thể tải danh sách địa chỉ');
      } finally {
        setLoading(false);
      }
    };

    fetchAddresses();
  }, [user]);

  useEffect(() => {
    setSelectedAddress(parentSelectedAddress);
  }, [parentSelectedAddress]);

  const handleAddressSelect = (address) => {
    const newSelectedAddress = {
      fullName: address.fullName,
      phone: address.phone,
      address: `${address.detail}, ${address.ward}, ${address.district}, ${address.province}`,
    };
    setSelectedAddress(newSelectedAddress);
    setIsAddressModalOpen(false);

    if (pendingProduct) {
      proceedToPayment(pendingProduct, newSelectedAddress);
      setPendingProduct(null);
    }
  };

  const handleNewAddressAdded = async (newAddress) => {
    const newSelectedAddress = {
      fullName: newAddress.fullName,
      phone: newAddress.phone,
      address: newAddress.address,
    };
    setSelectedAddress(newSelectedAddress);
    try {
      const token = user?.token;
      if (token) {
        const updatedAddresses = await AddressService.getAddresses(token);
        setAddresses(updatedAddresses || []);
      }
    } catch (error) {
      setError(`Không thể cập nhật danh sách địa chỉ: ${error.message}`);
    }
    setIsAddressFormOpen(false);
    setEditingAddress(null);
    setIsAddressModalOpen(false);

    if (pendingProduct) {
      proceedToPayment(pendingProduct, newSelectedAddress);
      setPendingProduct(null);
    }
  };

  const handleDeleteAddress = async (addressId) => {
    if (!user?.token) return;
    try {
      const token = user.token;
      const updatedAddresses = await AddressService.deleteAddress(addressId, token);
      setAddresses(updatedAddresses || []);
      if (
        selectedAddress?.address ===
        addresses.find((addr) => addr._id === addressId)?.address
      ) {
        setSelectedAddress(null);
      }
    } catch (error) {
      setError(`Không thể xóa địa chỉ: ${error.message}`);
    }
  };

  const handleEditAddress = (address) => {
    setEditingAddress(address);
    setIsAddressFormOpen(true);
  };

  const getPriceFromSchema = (product, size) => {
    if (!product || !product.prices) {
      console.error("Dữ liệu prices không tồn tại cho sản phẩm", product?.name);
      return undefined;
    }

    const dbSize = mapSizeToDatabase(size);
    const price = product.prices[dbSize]; // Truy cập trực tiếp từ prices (Map hoặc Object)

    console.log(`Giá cho trọng lượng ${size} (key: ${dbSize}):`, price);

    if (price === undefined) {
      console.error("Không tìm thấy giá cho trọng lượng", size, "của sản phẩm", product.name);
      return undefined;
    }
    return price;
  };

  const proceedToPayment = (product, address) => {
    const size = '0.25';
    const price = getPriceFromSchema(product, size);
    if (!price) {
      setError("Giá sản phẩm chưa được cập nhật. Vui lòng thử lại sau.");
      return;
    }

    const dbSize = mapSizeToDatabase(size);

    const item = {
      productId: product._id,
      name: product.name,
      price: price,
      image: product.image,
      size: dbSize,
      quantity: 1,
      selected: true,
    };

    navigate("/payment", {
      state: {
        selectedItems: [item],
        total: price * 1,
        selectedAddress: address,
      },
    });
    setError(null);
  };

  const handleBuyNow = (product) => {
    if (!user?.token) {
      setError("Vui lòng đăng nhập để tiếp tục mua hàng.");
      navigate('/sign-in');
      return;
    }

    if (!selectedAddress && addresses.length === 0) {
      setPendingProduct(product);
      setIsAddressModalOpen(true);
      return;
    }

    if (!selectedAddress) {
      setPendingProduct(product);
      setIsAddressModalOpen(true);
      return;
    }

    proceedToPayment(product, selectedAddress);
  };

  return (
    <div className="related-items">
      <h3>Sản phẩm cùng loại</h3>
      {loading && <p>Đang tải dữ liệu...</p>}
      {error && <p className="error-message">{error}</p>}

      <div className="related-list">
        {relatedProducts.length > 0 ? (
          relatedProducts
            .filter(product => product._id !== currentProductId)
            .map(product => {
              const size = '0.25';
              const displaySize = mapSizeToDisplay(size);
              const currentPrice = getPriceFromSchema(product, size);

              return (
                <div key={product._id} className="related-item">
                  <div
                    onClick={() => navigate(`/${product.category}/${product._id}`)}
                    style={{ cursor: 'pointer' }}
                  >
                    <img src={product.image} alt={product.name} className="related-image" />
                    <h4 className="related-name">{product.name}</h4>
                    <p className="related-price">
                      {currentPrice
                        ? `${currentPrice.toLocaleString()} VND / ${displaySize}`
                        : `Giá chưa cập nhật ${displaySize}`}
                    </p>
                  </div>
                  <div className="related-actions">
                    <button
                      className="btn-add"
                      onClick={(e) => {
                        e.stopPropagation();
                        if (!currentPrice) {
                          setError("Giá sản phẩm chưa được cập nhật. Vui lòng thử lại sau.");
                          return;
                        }
                        const dbSize = mapSizeToDatabase(size);
                        addToCart({
                          productId: product._id,
                          name: product.name,
                          price: currentPrice,
                          image: product.image,
                          size: dbSize,
                          quantity: 1,
                        });
                        setError(null);
                      }}
                      disabled={!currentPrice}
                    >
                      🛒
                    </button>
                    <button
                      className="btn-buy"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleBuyNow(product);
                      }}
                      disabled={!currentPrice}
                    >
                      Mua ngay
                    </button>
                  </div>
                </div>
              );
            })
        ) : (
          <p>Không có sản phẩm liên quan.</p>
        )}
      </div>

      {isAddressModalOpen && (
        <div className="address-modal-overlay">
          <div className="address-modal">
            <h4>Địa chỉ giao hàng</h4>
            {addresses.length === 0 ? (
              <div>
                <p>Chưa có địa chỉ giao hàng.</p>
                <button
                  onClick={() => setIsAddressFormOpen(true)}
                  className="add-address-btn"
                >
                  Thêm địa chỉ giao hàng
                </button>
              </div>
            ) : (
              <div className="address-list">
                <table className="address-table">
                  <thead>
                    <tr>
                      <th>Họ và tên</th>
                      <th>Địa chỉ</th>
                      <th>Số điện thoại</th>
                      <th>Hành động</th>
                    </tr>
                  </thead>
                  <tbody>
                    {addresses.map((address) => (
                      <tr key={address._id}>
                        <td>{address.fullName}</td>
                        <td>{`${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</td>
                        <td>{address.phone}</td>
                        <td>
                          <button
                            onClick={() => handleAddressSelect(address)}
                            className="select-address-btn"
                          >
                            Chọn
                          </button>
                          <button
                            onClick={() => handleEditAddress(address)}
                            className="edit-address-btn"
                          >
                            Sửa
                          </button>
                          <button
                            onClick={() => handleDeleteAddress(address._id)}
                            className="delete-address-btn"
                          >
                            Xóa
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <button
                  onClick={() => setIsAddressFormOpen(true)}
                  className="add-address-btn"
                >
                  Thêm địa chỉ mới
                </button>
              </div>
            )}
            <button
              onClick={() => {
                setIsAddressModalOpen(false);
                setPendingProduct(null);
              }}
              className="close-modal-btn"
            >
              Đóng
            </button>
          </div>
        </div>
      )}

      {isAddressFormOpen && (
        <div className="address-form-overlay">
          <ShippingAddressForm
            onAddressSelect={handleNewAddressAdded}
            onClose={() => {
              setIsAddressFormOpen(false);
              setEditingAddress(null);
            }}
            initialData={editingAddress}
          />
        </div>
      )}
    </div>
  );
};

export default RelatedItems;
