import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const [user, setUser] = useState(null);

    useEffect(() => {
        if (user) {
            fetchCartFromServer(user.id);
        } else {
            loadCartFromLocalStorage();
        }
    }, [user]);

    useEffect(() => {
        if (!user) {
            saveCartToLocalStorage();
        }
    }, [cartItems, user]);

    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    const loadCartFromLocalStorage = () => {
        try {
            const savedCart = localStorage.getItem('cartItems');
            setCartItems(savedCart ? JSON.parse(savedCart) : []);
        } catch (error) {
            console.error("Lỗi khi lấy giỏ hàng từ localStorage:", error);
            setCartItems([]);
        }
    };

    const saveCartToLocalStorage = () => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    };

    const fetchCartFromServer = async (userId) => {
        try {
            const res = await fetch(`http://localhost:5000/cart/${userId}`);
            const data = await res.json();
            setCartItems(data.items || []);
        } catch (error) {
            console.error("Lỗi khi lấy giỏ hàng từ server:", error);
        }
    };

    return (
        <CartContext.Provider
            value={{
                cartItems,
                setCartItems,
                isOpen,
                toggleCart,
                fetchCartFromServer,
                loadCartFromLocalStorage,
                saveCartToLocalStorage
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
