import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import ProductItem from '../components/ProductItem';
import RelatedItem from '../components/RelatedItem';
import Footer from './Footer';
import '../styles/ProductDetail.css';

const ProductDetail = ({ addToCart }) => {
    const { category, id } = useParams();
    const [product, setProduct] = useState(null);
    const [relatedProducts, setRelatedProducts] = useState([]);

    useEffect(() => {
        // Gọi API lấy thông tin sản phẩm theo ID
        fetch(`http://localhost:5000/api/products/${id}`)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => console.error('Lỗi tải sản phẩm:', err));

        // Gọi API lấy sản phẩm liên quan cùng danh mục (trừ sản phẩm hiện tại)
        fetch(`http://localhost:5000/api/products?category=${category}`)
            .then(res => res.json())
            .then(data => setRelatedProducts(data.filter(p => p._id !== id)))
            .catch(err => console.error('Lỗi tải sản phẩm liên quan:', err));
    }, [category, id]);

    if (!product) {
        return <div>Đang tải sản phẩm...</div>;
    }

    return (
        <div className="product-detail">
            <Header />
            <div className="product-content">
                <ProductItem product={product} addToCart={addToCart} />
                <RelatedItem relatedProducts={relatedProducts} />
            </div>
            <Footer />
        </div>
    );
};

export default ProductDetail;
