// src/context/CartContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState(() => {
        const savedCart = localStorage.getItem('cartItems');
        return savedCart ? JSON.parse(savedCart) : [];
    });

    const [isOpen, setIsOpen] = useState(false);

    const addToCart = (name, price, image) => {
        setCartItems(prev => {
            const existingItem = prev.find(item => item.name === name);
            let updatedCart;
            if (existingItem) {
                updatedCart = prev.map(item =>
                    item.name === name
                        ? { ...item, quantity: item.quantity + 1 }
                        : item
                );
            } else {
                updatedCart = [...prev, { name, price, image, quantity: 1, selected: true }];
            }
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
        setIsOpen(true);
    };

    const removeFromCart = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.filter((_, i) => i !== index);
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    const increaseQuantity = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, quantity: item.quantity + 1 } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const decreaseQuantity = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index && item.quantity > 1
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const toggleItemSelection = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.map((item, i) =>
                i === index ? { ...item, selected: !item.selected } : item
            );
            localStorage.setItem('cartItems', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const clearCart = () => {
        setCartItems([]); // Xóa toàn bộ giỏ hàng
        localStorage.removeItem('cartItems'); // Xóa dữ liệu trong localStorage
    };

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);
    const total = cartItems.reduce(
        (sum, item) => (item.selected ? sum + item.price * item.quantity : sum),
        0
    );

    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider
            value={{
                cartItems,
                addToCart,
                removeFromCart,
                isOpen,
                toggleCart,
                total,
                totalItems,
                increaseQuantity,
                decreaseQuantity,
                toggleItemSelection,
                clearCart, // Thêm clearCart vào context
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
