// src/context/CartContext.js
import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { useAuth } from './AuthContext';
import { fetchCart, addToCart } from '../services/CartService';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const [cartItems, setCartItems] = useState([]);
    const [isOpen, setIsOpen] = useState(false);
    const { user } = useAuth();

    const loadCartFromServer = useCallback(async () => {
        if (user && user._id) {
            try {
                const cart = await fetchCart(user._id);
                const itemsWithSelection = cart.items.map(item => ({
                    ...item,
                    selected: item.selected || false
                }));
                setCartItems(itemsWithSelection);
            } catch (error) {
                setCartItems([]);
            }
        }
    }, [user]);

    const loadCartFromLocalStorage = useCallback(() => {
        try {
            const savedCart = localStorage.getItem('cartItems');
            setCartItems(savedCart ? JSON.parse(savedCart) : []);
        } catch (error) {
            console.error("Lỗi khi lấy giỏ hàng từ localStorage:", error);
            setCartItems([]);
        }
    }, []);

    useEffect(() => {
        if (user && user._id) {
            loadCartFromServer();
        } else {
            console.warn('Không có user._id, sử dụng localStorage:', user);
            loadCartFromLocalStorage();
        }
    }, [user, loadCartFromServer, loadCartFromLocalStorage]);

    useEffect(() => {
        if (!user || !user._id) {
            localStorage.setItem('cartItems', JSON.stringify(cartItems));
        }
    }, [cartItems, user]);

    const toggleCart = () => setIsOpen(prev => !prev);

    const handleAddToCart = async (product) => {
        const { productId, quantity = 1, size, name, price, image } = product;

        if (user && user._id) {
            try {
                await addToCart(user._id, { productId, quantity, size, name, price, image });
                await loadCartFromServer();
            } catch (error) {
                // Lỗi đã được log trong CartService
            }
        } else {
            setCartItems(prev => {
                const existingItem = prev.find(item => item.productId === productId && item.size === size);
                if (existingItem) {
                    return prev.map(item =>
                        item.productId === productId && item.size === size
                            ? { ...item, quantity: item.quantity + quantity }
                            : item
                    );
                }
                return [...prev, { productId, quantity, size, name, price, image, selected: false }];
            });
        }
    };

    const total = cartItems
        .filter(item => item.selected)
        .reduce((sum, item) => sum + (item.price * item.quantity), 0);

    const totalItems = cartItems.reduce((sum, item) => sum + item.quantity, 0);

    const displaySize = (size) => {
        switch (size) {
            case "0.25": return "0.25kg";
            case "0.5": return "0.5kg";
            case "1": return "1kg";
            default: return size;
        }
    };

    return (
        <CartContext.Provider
            value={{
                cartItems,
                setCartItems,
                isOpen,
                toggleCart,
                total,
                totalItems,
                addToCart: handleAddToCart,
                fetchCartFromServer: loadCartFromServer,
                loadCartFromLocalStorage,
                displaySize
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error("useCart must be used within a CartProvider");
    }
    return context;
};
