import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/menu.css';
import { fetchMenuItems } from '../services/menuService';
import { useCart } from '../context/CartContext';

function Menu() {
    const [activeCategory, setActiveCategory] = useState('fruit');
    const [menuItems, setMenuItems] = useState([]);
    const { addToCart } = useCart();
    const navigate = useNavigate();

    // Trạng thái để chọn trọng lượng cho mỗi sản phẩm
    const [selectedWeights, setSelectedWeights] = useState({});

    // Hàm ánh xạ giá trị weight từ UI sang giá trong prices
    const getPriceFromSchema = (item, weight) => {
        if (weight === "1") {
            return item.prices?.["1"];
        } else {
            return item.prices?.["0"]?.[weight];
        }
    };

    useEffect(() => {
        fetchMenuItems(activeCategory).then((items) => {
            setMenuItems(items);
            // Khởi tạo selectedWeights cho từng sản phẩm
            const initialWeights = items.reduce((acc, item) => {
                acc[item._id] = "25"; // Mặc định là 0.25kg
                return acc;
            }, {});
            setSelectedWeights(initialWeights);
        });
    }, [activeCategory]);

    const handleAddToCart = (item) => {
        const weight = selectedWeights[item._id] || "25"; // Lấy trọng lượng đã chọn hoặc mặc định
        const price = getPriceFromSchema(item, weight);
        if (price === undefined) {
            console.error("Lỗi: Không tìm thấy giá sản phẩm", item);
            return;
        }

        // Chuyển đổi weight thành size phù hợp với backend (0.25, 0.5, 1)
        const size = weight === "25" ? "0.25" : weight === "5" ? "0.5" : "1";

        addToCart({
            productId: item._id, // Thêm productId (bắt buộc cho backend)
            name: item.name,
            price: price,
            image: item.image,
            size: size, // Dùng "size" thay vì "weight" để khớp với CartService/CartContext
            quantity: 1
        });
    };

    const handleWeightChange = (itemId, weight) => {
        setSelectedWeights(prev => ({
            ...prev,
            [itemId]: weight
        }));
    };

    return (
        <section className="menu section-padding" id="dactrung">
            <div className="container">
                <div className="row">
                    <div className="section-title">
                        <h2 data-title="Món Đặc Trưng">Thực đơn của chúng tôi</h2>
                    </div>
                </div>

                <div className="row">
                    <div className="menu-title">
                        <h2>Ẩm thực Thái Lan mang một sự quyến rũ đặc biệt...</h2>
                        <nav className="menu-nav">
                            <ul>
                                {["fruit", "seafood", "meat", "nut"].map((category) => (
                                    <li key={category}>
                                        <button
                                            onClick={() => setActiveCategory(category)}
                                            className={`menu-button ${activeCategory === category ? 'active' : ''}`}
                                        >
                                            {category === "fruit" && "Trái Cây Sấy"}
                                            {category === "seafood" && "Hải Sản Sấy"}
                                            {category === "meat" && "Thịt Sấy"}
                                            {category === "nut" && "Hạt Sấy"}
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </nav>
                    </div>
                </div>

                <div className="row justify-content">
                    <div id={activeCategory} className="menu-content show">
                        {menuItems.map((item) => (
                            <div className="row food-items" key={item._id}>
                                <div
                                    className="row food-item"
                                    onClick={() => navigate(`/${item.category}/${item._id}`)}
                                    style={{ cursor: "pointer" }}
                                >
                                    <img src={item.image} alt={item.name} />
                                    <h2>{item.name}</h2>
                                </div>
                                <div className="food-price">
                                    <p>
                                        {getPriceFromSchema(item, selectedWeights[item._id] || "25")
                                            ? `${getPriceFromSchema(item, selectedWeights[item._id] || "25").toLocaleString()} VND / ${selectedWeights[item._id] === "1" ? "1kg" : selectedWeights[item._id] === "5" ? "0.5kg" : "0.25kg"}`
                                            : "Giá chưa cập nhật"}
                                    </p>
                                </div>
                                <div className="weight-selector">
                                    <select
                                        value={selectedWeights[item._id] || "25"}
                                        onChange={(e) => handleWeightChange(item._id, e.target.value)}
                                        onClick={(e) => e.stopPropagation()} // Ngăn click xuyên qua div food-item
                                    >
                                        <option value="25">0.25kg</option>
                                        <option value="5">0.5kg</option>
                                        <option value="1">1kg</option>
                                    </select>
                                </div>
                                <button
                                    className="add-to-cart-btn"
                                    onClick={(e) => {
                                        e.stopPropagation(); // Ngăn click xuyên qua div food-item
                                        handleAddToCart(item);
                                    }}
                                >
                                    🛒
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Menu;
