// components/Cart.jsx
import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import ShippingAddressForm from './ShippingAddressForm';
import AddressService from '../services/AddressService';
import '../styles/Cart.css';

const Cart = () => {
  const {
    cartItems,
    total,
    totalItems,
    increaseQuantity,
    decreaseQuantity,
    removeFromCart,
    selectAll,
    toggleItemSelection,
    displaySize,
  } = useCart();

  const { user, logout } = useAuth();
  const [isAddressFormOpen, setIsAddressFormOpen] = useState(false);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [error, setError] = useState(null);

  // Lấy danh sách địa chỉ từ MongoDB khi component được mount
  useEffect(() => {
    const fetchAddresses = async () => {
      if (!user || !user._id) {
        setError('Vui lòng đăng nhập để xem địa chỉ giao hàng.');
        return;
      }

      try {
        const addressList = await AddressService.getAddresses();
        setAddresses(addressList);
        // Chọn địa chỉ mặc định (nếu có)
        const defaultAddress = await AddressService.getDefaultAddress();
        if (defaultAddress) {
          setSelectedAddress({
            fullName: defaultAddress.fullName,
            phone: defaultAddress.phone,
            address: `${defaultAddress.detail}, ${defaultAddress.ward}, ${defaultAddress.district}, ${defaultAddress.province}`,
          });
        }
      } catch (error) {
        if (error.message === 'Token không tồn tại. Vui lòng đăng nhập lại.') {
          setError('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
          logout();
        } else {
          setError('Không thể tải danh sách địa chỉ. Vui lòng thử lại sau.');
        }
      }
    };

    fetchAddresses();
  }, [user, logout]);

  // Xử lý khi người dùng chọn một địa chỉ từ danh sách
  const handleAddressSelect = (address) => {
    setSelectedAddress({
      fullName: address.fullName,
      phone: address.phone,
      address: `${address.detail}, ${address.ward}, ${address.district}, ${address.province}`,
    });
  };

  // Xử lý khi người dùng thêm địa chỉ mới
  const handleNewAddressAdded = async (newAddress) => {
    setSelectedAddress({
      fullName: newAddress.fullName,
      phone: newAddress.phone,
      address: `${newAddress.detail}, ${newAddress.ward}, ${newAddress.district}, ${newAddress.province}`,
    });
    // Cập nhật lại danh sách địa chỉ
    try {
      const updatedAddresses = await AddressService.getAddresses();
      setAddresses(updatedAddresses);
    } catch (error) {
      setError('Không thể cập nhật danh sách địa chỉ. Vui lòng thử lại.');
    }
    setIsAddressFormOpen(false);
  };

  const handleSelectAllChange = (e) => {
    selectAll(e.target.checked);
  };

  return (
    <div className="cart-container">
      {/* Header */}
      <div className="cart-header">
        <div className="shop-logo">
          <h1>Hương Việt Tinh</h1>
          <p>Giỏ Hàng ({totalItems} sản phẩm)</p>
        </div>
      </div>

      {/* Address Section */}
      <div className="address-section">
        <h3>Địa chỉ giao hàng</h3>
        {error ? (
          <p className="error-message">{error}</p>
        ) : addresses.length === 0 ? (
          <div>
            <p>Chưa có địa chỉ giao hàng.</p>
            <button onClick={() => setIsAddressFormOpen(true)} className="add-address-btn">
              Thêm địa chỉ giao hàng
            </button>
          </div>
        ) : (
          <div className="address-list">
            {selectedAddress ? (
              <div className="selected-address">
                <p>
                  <strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})
                </p>
                <p>{selectedAddress.address}</p>
                <button onClick={() => setIsAddressFormOpen(true)} className="change-address-btn">
                  Thay đổi
                </button>
              </div>
            ) : (
              <p>Chưa chọn địa chỉ giao hàng.</p>
            )}
            {isAddressFormOpen ? (
              <ShippingAddressForm
                onAddressSelect={handleNewAddressAdded}
                onClose={() => setIsAddressFormOpen(false)}
              />
            ) : (
              <div className="address-options">
                <h4>Chọn địa chỉ:</h4>
                {addresses.map((address) => (
                  <div
                    key={address._id}
                    className="address-item"
                    onClick={() => handleAddressSelect(address)}
                  >
                    <p>
                      <strong>{address.fullName}</strong> ({address.phone})
                    </p>
                    <p>{`${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</p>
                  </div>
                ))}
                <button onClick={() => setIsAddressFormOpen(true)} className="add-address-btn">
                  Thêm địa chỉ mới
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Cart Items */}
      <div className="cart-items">
        <div className="cart-item-header">
          <input
            type="checkbox"
            onChange={handleSelectAllChange}
            checked={cartItems.length > 0 && cartItems.every((item) => item.selected)}
          />
          <span>Sản Phẩm</span>
          <span>Phân Loại Hàng</span>
          <span>Đơn Giá</span>
          <span>Số Lượng</span>
          <span>Số Tiền</span>
          <span>Thao Tác</span>
        </div>

        {cartItems.length === 0 ? (
          <p className="empty-cart">Giỏ hàng của bạn đang trống.</p>
        ) : (
          cartItems.map((item) => (
            <div key={`${item.productId}-${item.size}`} className="cart-item">
              <input
                type="checkbox"
                checked={item.selected}
                onChange={() => toggleItemSelection(item.productId, item.size)}
              />
              <div className="cart-item-details">
                <img src={item.image} alt={item.name} className="cart-item-image" />
                <div className="cart-item-info">
                  <p>{item.name}</p>
                  <p className="cart-item-classification">
                    Phân Loại Hàng: {item.color || 'N/A'}, {displaySize(item.size)}
                  </p>
                </div>
              </div>
              <div className="cart-item-classification">
                {item.color || 'N/A'}, {displaySize(item.size)}
              </div>
              <div className="cart-item-price">{item.price.toLocaleString()}đ</div>
              <div className="cart-item-quantity">
                <button onClick={() => decreaseQuantity(item.productId, item.size)}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => increaseQuantity(item.productId, item.size)}>+</button>
              </div>
              <div className="cart-item-total">{(item.price * item.quantity).toLocaleString()}đ</div>
              <div className="cart-item-actions">
                <button onClick={() => removeFromCart(item.productId, item.size)}>Xóa</button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Cart Summary */}
      <div className="cart-summary">
        <div className="summary-details">
          <p>
            Tổng thanh toán ({cartItems.filter((item) => item.selected).length} sản phẩm):{' '}
            <span>{total.toLocaleString()}đ</span>
          </p>
        </div>
        <button className="checkout-btn" disabled={total === 0 || !selectedAddress}>
          Đặt Hàng
        </button>
      </div>
    </div>
  );
};

export default Cart;
