import React from 'react';
import { Route, Routes } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart, removeFromCart } from './redux/cartSlice';
import Header from './components/Header';
import BigImage from './components/BigImage';
import About from './components/About';
import Menu from './components/Menu';
import CartSidebar from './components/CartSidebar';
import Team from './components/Team';
import SignIn from './components/SignIn';

function App() {
    // Lấy cartItems từ Redux store
    const cartItems = useSelector((state) => state.cart.cartItems);
    const dispatch = useDispatch();

    // Hàm thêm vào giỏ hàng
    const handleAddToCart = (name, price, image) => {
        dispatch(addToCart({ name, price, image }));
    };

    // Hàm xóa món khỏi giỏ hàng
    const handleRemoveFromCart = (index) => {
        dispatch(removeFromCart(index));
    };

    return (
        <Routes>
            <Route
                path="/"
                element={
                    <>
                        <Header cartItems={cartItems} />
                        <BigImage />
                        <About />
                        <Menu addToCart={handleAddToCart} />
                        <CartSidebar cartItems={cartItems} removeFromCart={handleRemoveFromCart} />
                        <Team />
                        <footer>
                            <h2>Liên Hệ</h2>
                            <p>Email: contact@example.com</p>
                            <p>Phone: 0123-456-789</p>
                        </footer>
                    </>
                }
            />
            <Route path="/menu" element={<Menu addToCart={handleAddToCart} />} />
            <Route path="/about" element={<About />} />
            <Route path="/sign-in" element={<SignIn />} />
        </Routes>
    );
}

export default App;
