// components/ShippingAddressForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddressService from '../services/AddressService'; // Cập nhật import
import { useAuth } from '../context/AuthContext';

function ShippingAddressForm({ onAddressSelect }) {
    const { user } = useAuth(); // Lấy thông tin user từ AuthContext
    const [formData, setFormData] = useState({
        province: '',
        district: '',
        ward: '',
        detail: '',
        fullName: '',
        phone: ''
    });
    const [errors, setErrors] = useState({});
    const [savedAddresses, setSavedAddresses] = useState([]);
    const [selectedAddressId, setSelectedAddressId] = useState(null); // Thay đổi từ index sang id
    const [isEditing, setIsEditing] = useState(false);

    const [provinces, setProvinces] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [wards, setWards] = useState([]);

    useEffect(() => {
        const fetchAddresses = async () => {
            if (user) {
                try {
                    const addresses = await AddressService.getAddresses();
                    setSavedAddresses(addresses);
                } catch (error) {
                    setSavedAddresses([]);
                }
            }
        };
        fetchAddresses();
    }, [user]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const provinceName = provinces.find(p => p.code === parseInt(formData.province))?.name || '';
        const districtName = districts.find(d => d.code === parseInt(formData.district))?.name || '';
        const wardName = wards.find(w => w.code === parseInt(formData.ward))?.name || '';
        const addressData = {
            fullName: formData.fullName,
            phone: formData.phone,
            province: provinceName,
            district: districtName,
            ward: wardName,
            detail: formData.detail
        };

        try {
            if (isEditing && selectedAddressId) {
                const updatedAddress = await AddressService.updateAddress(selectedAddressId, addressData);
                const updatedAddresses = savedAddresses.map(addr =>
                    addr._id === selectedAddressId ? updatedAddress : addr
                );
                setSavedAddresses(updatedAddresses);
                setIsEditing(false);
            } else {
                const newAddress = await AddressService.addAddress(addressData);
                setSavedAddresses([...savedAddresses, newAddress]);
            }

            onAddressSelect({
                fullName: addressData.fullName,
                address: `${addressData.detail}, ${addressData.ward}, ${addressData.district}, ${addressData.province}`,
                phone: addressData.phone
            });

            setFormData({
                province: '',
                district: '',
                ward: '',
                detail: '',
                fullName: '',
                phone: ''
            });
            setSelectedAddressId(null);
        } catch (error) {
            setErrors({ form: error.response?.data?.message || 'Lỗi khi lưu địa chỉ' });
        }
    };

    const handleEdit = (addressId) => {
        const addressToEdit = savedAddresses.find(addr => addr._id === addressId);
        if (addressToEdit) {
            setFormData({
                province: addressToEdit.province, // Cần map ngược lại code nếu có danh sách provinces
                district: addressToEdit.district,
                ward: addressToEdit.ward,
                detail: addressToEdit.detail,
                fullName: addressToEdit.fullName,
                phone: addressToEdit.phone
            });
            setSelectedAddressId(addressId);
            setIsEditing(true);
        }
    };

    const handleDelete = async (addressId) => {
        try {
            const updatedAddresses = await AddressService.deleteAddress(addressId);
            setSavedAddresses(updatedAddresses);
        } catch (error) {
            setErrors({ form: 'Lỗi khi xóa địa chỉ' });
        }
    };

    return (
        <div>
            <h2>Địa chỉ giao hàng</h2>
            {user && <p>User ID: {user.id}</p>}
            {/* Hiển thị danh sách địa chỉ */}
            <div>
                {savedAddresses.map((address) => (
                    <div key={address._id}>
                        <p>{`${address.fullName}, ${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</p>
                        <p>{address.phone}</p>
                        <button onClick={() => handleEdit(address._id)}>Sửa</button>
                        <button onClick={() => handleDelete(address._id)}>Xóa</button>
                    </div>
                ))}
            </div>
            {/* Form thêm/cập nhật địa chỉ */}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Họ và tên:</label>
                    <input
                        type="text"
                        value={formData.fullName}
                        onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                    />
                </div>
                <div>
                    <label>Số điện thoại:</label>
                    <input
                        type="text"
                        value={formData.phone}
                        onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                    />
                </div>
                <div>
                    <label>Tỉnh/Thành phố:</label>
                    <select
                        value={formData.province}
                        onChange={(e) => setFormData({ ...formData, province: e.target.value })}
                    >
                        <option value="">Chọn Tỉnh/Thành phố</option>
                        {provinces.map((province) => (
                            <option key={province.code} value={province.code}>
                                {province.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Quận/Huyện:</label>
                    <select
                        value={formData.district}
                        onChange={(e) => setFormData({ ...formData, district: e.target.value })}
                    >
                        <option value="">Chọn Quận/Huyện</option>
                        {districts.map((district) => (
                            <option key={district.code} value={district.code}>
                                {district.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Phường/Xã:</label>
                    <select
                        value={formData.ward}
                        onChange={(e) => setFormData({ ...formData, ward: e.target.value })}
                    >
                        <option value="">Chọn Phường/Xã</option>
                        {wards.map((ward) => (
                            <option key={ward.code} value={ward.code}>
                                {ward.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label>Địa chỉ chi tiết:</label>
                    <input
                        type="text"
                        value={formData.detail}
                        onChange={(e) => setFormData({ ...formData, detail: e.target.value })}
                    />
                </div>
                {errors.form && <p style={{ color: 'red' }}>{errors.form}</p>}
                <button type="submit">{isEditing ? 'Cập nhật' : 'Thêm địa chỉ'}</button>
            </form>
        </div>
    );
}

export default ShippingAddressForm;
