import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Select from 'react-select';
import AddressService from '../services/AddressService'; // Import AddressService

function ShippingAddressForm({ onAddressSelect }) {
    // State để lưu dữ liệu form
    const [formData, setFormData] = useState({
        province: null,
        district: null,
        ward: null,
        detail: '',
        fullName: '',
        phone: ''
    });

    // State để lưu danh sách địa chỉ đã lưu
    const [savedAddresses, setSavedAddresses] = useState([]);
    const [selectedAddressIndex, setSelectedAddressIndex] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    // State để lưu danh sách Tỉnh, Quận/Huyện, Phường/Xã
    const [provinces, setProvinces] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [wards, setWards] = useState([]);

    // Lấy danh sách địa chỉ đã lưu khi component mount
    useEffect(() => {
        const addresses = AddressService.getAddresses();
        setSavedAddresses(addresses);
    }, []);

    // Lấy danh sách Tỉnh/Thành phố khi component mount
    useEffect(() => {
        axios.get('https://provinces.open-api.vn/api/p/')
            .then(response => {
                const provinceOptions = response.data.map(province => ({
                    value: province.code,
                    label: province.name
                }));
                setProvinces(provinceOptions);
            })
            .catch(error => console.error('Lỗi khi lấy danh sách Tỉnh:', error));
    }, []);

    // Lấy danh sách Quận/Huyện khi chọn Tỉnh
    useEffect(() => {
        if (formData.province) {
            axios.get(`https://provinces.open-api.vn/api/p/${formData.province.value}?depth=2`)
                .then(response => {
                    const districtOptions = response.data.districts.map(district => ({
                        value: district.code,
                        label: district.name
                    }));
                    setDistricts(districtOptions);
                    setFormData({ ...formData, district: null, ward: null });
                    setWards([]);
                })
                .catch(error => console.error('Lỗi khi lấy danh sách Quận/Huyện:', error));
        }
    }, [formData.province]);

    // Lấy danh sách Phường/Xã khi chọn Quận/Huyện
    useEffect(() => {
        if (formData.district) {
            axios.get(`https://provinces.open-api.vn/api/d/${formData.district.value}?depth=2`)
                .then(response => {
                    const wardOptions = response.data.wards.map(ward => ({
                        value: ward.code,
                        label: ward.name
                    }));
                    setWards(wardOptions);
                    setFormData({ ...formData, ward: null });
                })
                .catch(error => console.error('Lỗi khi lấy danh sách Phường/Xã:', error));
        }
    }, [formData.district]);

    // Xử lý thay đổi giá trị trong form
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Xử lý khi submit form
    const handleSubmit = (e) => {
        e.preventDefault();
        const fullAddress = `${formData.detail}, ${formData.ward?.label}, ${formData.district?.label}, ${formData.province?.label}`;
        const addressData = {
            fullName: formData.fullName,
            address: fullAddress,
            phone: formData.phone
        };

        if (isEditing && selectedAddressIndex !== null) {
            // Cập nhật địa chỉ đã có
            const updatedAddresses = AddressService.updateAddress(selectedAddressIndex, addressData);
            setSavedAddresses(updatedAddresses);
            setIsEditing(false);
        } else {
            // Thêm địa chỉ mới
            const updatedAddresses = AddressService.addAddress(addressData);
            setSavedAddresses(updatedAddresses);
        }

        // Gọi callback để thông báo địa chỉ đã chọn
        onAddressSelect(addressData);

        // Reset form
        setFormData({
            province: null,
            district: null,
            ward: null,
            detail: '',
            fullName: '',
            phone: ''
        });
        setSelectedAddressIndex(null);
    };

    // Xử lý khi chọn một địa chỉ đã lưu
    const handleSelectAddress = (index) => {
        setSelectedAddressIndex(index);
        onAddressSelect(savedAddresses[index]); // Gọi callback để thông báo địa chỉ đã chọn
    };

    // Xử lý khi chỉnh sửa một địa chỉ
    const handleEditAddress = (index) => {
        const address = savedAddresses[index];
        setSelectedAddressIndex(index);
        setIsEditing(true);

        // Tạm thời không thể parse lại địa chỉ chi tiết thành các trường Tỉnh, Quận, Phường
        // Bạn có thể thêm logic để parse nếu cần
        setFormData({
            province: null,
            district: null,
            ward: null,
            detail: address.address.split(', ')[0], // Lấy phần chi tiết
            fullName: address.fullName,
            phone: address.phone
        });
    };

    // Xử lý khi xóa một địa chỉ
    const handleDeleteAddress = (index) => {
        const updatedAddresses = AddressService.deleteAddress(index);
        setSavedAddresses(updatedAddresses);
        setSelectedAddressIndex(null);
    };

    return (
        <div>
            {/* Hiển thị danh sách địa chỉ đã lưu */}
            {savedAddresses.length > 0 && (
                <div style={{ marginBottom: '20px' }}>
                    <h3>Địa chỉ đã lưu</h3>
                    {savedAddresses.map((address, index) => (
                        <div
                            key={index}
                            style={{
                                padding: '10px',
                                border: '1px solid #ddd',
                                marginBottom: '10px',
                                backgroundColor: selectedAddressIndex === index ? '#e0f7fa' : '#fff'
                            }}
                        >
                            <p><strong>{address.fullName}</strong></p>
                            <p>{address.address}</p>
                            <p>Số điện thoại: {address.phone}</p>
                            <button
                                onClick={() => handleSelectAddress(index)}
                                style={{ marginRight: '10px', padding: '5px 10px', backgroundColor: '#4CAF50', color: 'white', border: 'none' }}
                            >
                                Chọn
                            </button>
                            <button
                                onClick={() => handleEditAddress(index)}
                                style={{ marginRight: '10px', padding: '5px 10px', backgroundColor: '#FFC107', color: 'white', border: 'none' }}
                            >
                                Sửa
                            </button>
                            <button
                                onClick={() => handleDeleteAddress(index)}
                                style={{ padding: '5px 10px', backgroundColor: '#F44336', color: 'white', border: 'none' }}
                            >
                                Xóa
                            </button>
                        </div>
                    ))}
                </div>
            )}

            {/* Form nhập địa chỉ */}
            <form onSubmit={handleSubmit} style={{ maxWidth: '500px', margin: '0 auto', padding: '20px' }}>
                <h2>{isEditing ? 'Chỉnh sửa địa chỉ' : 'Thêm địa chỉ mới'}</h2>

                <div style={{ marginBottom: '15px' }}>
                    <label>Họ và tên:</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Tỉnh/Thành phố:</label>
                    <Select
                        options={provinces}
                        value={formData.province}
                        onChange={(selectedOption) => setFormData({ ...formData, province: selectedOption })}
                        placeholder="Chọn Tỉnh/Thành phố"
                        required
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Quận/Huyện:</label>
                    <Select
                        options={districts}
                        value={formData.district}
                        onChange={(selectedOption) => setFormData({ ...formData, district: selectedOption })}
                        placeholder="Chọn Quận/Huyện"
                        isDisabled={!formData.province}
                        required
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Phường/Xã:</label>
                    <Select
                        options={wards}
                        value={formData.ward}
                        onChange={(selectedOption) => setFormData({ ...formData, ward: selectedOption })}
                        placeholder="Chọn Phường/Xã"
                        isDisabled={!formData.district}
                        required
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Chi tiết (số nhà, tên đường):</label>
                    <input
                        type="text"
                        name="detail"
                        value={formData.detail}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Số điện thoại:</label>
                    <input
                        type="tel"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                    />
                </div>

                <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
                    {isEditing ? 'Cập nhật địa chỉ' : 'Thêm địa chỉ'}
                </button>
            </form>
        </div>
    );
}

export default ShippingAddressForm;
