import React from 'react';
import { useNavigate } from 'react-router-dom';
import "../styles/RelatedItem.css";
import { useCart } from '../context/CartContext'; // Import useCart

const RelatedItems = ({ relatedProducts, currentProductId }) => {
  const { addToCart } = useCart(); // Lấy addToCart từ CartContext
  const navigate = useNavigate();

  return (
    <div className="related-items">
      <h3>Sản phẩm cùng loại</h3>
      <div className="related-list">
        {relatedProducts.length > 0 ? (
          relatedProducts
            .filter(product => product._id !== currentProductId)
            .map(product => (
              <div key={product._id} className="related-item">
                <div
                  onClick={() => navigate(`/${product.category}/${product._id}`)}
                  style={{ cursor: 'pointer' }}
                >
                  <img src={product.image} alt={product.name} className="related-image" />
                  <h4 className="related-name">{product.name}</h4>
                  <p className="related-price">
                    {product.prices?.["1"] 
                      ? `${product.prices["1"].toLocaleString()} VND / 1kg`
                      : "Giá chưa cập nhật"}
                  </p>
                </div>
                <button
                  className="btn-add"
                  onClick={(e) => {
                    e.stopPropagation(); // Ngăn sự kiện click trên div cha (chuyển hướng)
                    addToCart({ 
                      productId: product._id, // Giả sử product có _id từ database
                      name: product.name, 
                      price: product.prices?.["1"], 
                      image: product.image, 
                      size: "1", // Giá trị mặc định là 1kg
                      quantity: 1 
                    });
                  }}
                >
                  Thêm vào giỏ
                </button>
              </div>
            ))
        ) : (
          <p>Không có sản phẩm liên quan.</p>
        )}
      </div>
    </div>
  );
};

export default RelatedItems;
