// src/context/CartContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
    // Khôi phục cartItems từ localStorage khi khởi động
    const [cartItems, setCartItems] = useState(() => {
        const savedCart = localStorage.getItem('cartItems');
        return savedCart ? JSON.parse(savedCart) : [];
    });

    const [isOpen, setIsOpen] = useState(false);

    const addToCart = (name, price, image) => {
        setCartItems(prev => {
            const updatedCart = [...prev, { name, price, image }];
            localStorage.setItem('cartItems', JSON.stringify(updatedCart)); // Lưu vào localStorage
            return updatedCart;
        });
        setIsOpen(true); // Mở sidebar khi thêm sản phẩm
    };

    const removeFromCart = (index) => {
        setCartItems(prev => {
            const updatedCart = prev.filter((_, i) => i !== index);
            localStorage.setItem('cartItems', JSON.stringify(updatedCart)); // Cập nhật localStorage
            return updatedCart;
        });
    };

    const toggleCart = () => {
        setIsOpen(prev => !prev);
    };

    const total = cartItems.reduce((sum, item) => sum + item.price, 0);

    // Đồng bộ với localStorage khi cartItems thay đổi
    useEffect(() => {
        localStorage.setItem('cartItems', JSON.stringify(cartItems));
    }, [cartItems]);

    return (
        <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, isOpen, toggleCart, total }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
