import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Header.css';

function Header({ cartItems }) {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const scrollToSection = (sectionId) => {
        const section = document.getElementById(sectionId);
        if (section) {
            section.scrollIntoView({ behavior: 'smooth' });
        }
        setMenuOpen(false); // Đóng menu trên mobile
    };

    return (
        <header className="header">
            <div className="container">
                <div className="header-content">
                    {/* Logo */}
                    <div className="logo">
                        <button onClick={() => scrollToSection('big-image-section')}>
                            <img
                                src="/images/TMKLogo2020Xanh.png"
                                alt="Thai Market Logo"
                                onError={(e) => (e.target.src = 'https://via.placeholder.com/150')}
                            />
                        </button>
                    </div>

                    {/* Navigation */}
                    <nav className={`nav-menu ${menuOpen ? 'open' : ''}`}>
                        <ul>
                            <li>
                                <button
                                    className="nav-link-btn"
                                    onClick={() => scrollToSection('big-image-section')}
                                >
                                    Trang Chủ
                                </button>
                            </li>
                            <li>
                                <button
                                    className="nav-link-btn"
                                    onClick={() => scrollToSection('menu-section')}
                                >
                                    Sản Phẩm
                                </button>
                            </li>
                            <li>
                                <button
                                    className="nav-link-btn"
                                    onClick={() => scrollToSection('about-section')}
                                >
                                    Về Chúng Tôi
                                </button>
                            </li>
                            <li>
                                <button
                                    className="nav-link-btn"
                                    onClick={() => scrollToSection('footer')}
                                >
                                    Liên Hệ
                                </button>
                            </li>
                        </ul>
                    </nav>

                    {/* User Actions */}
                    <div className="user-actions">
                        <Link to="/sign-in" className="sign-in-btn">
                            Đăng Nhập
                        </Link>
                        <button
                            className="cart-icon"
                            onClick={() => scrollToSection('cart-sidebar')}
                        >
                            <i className="fas fa-shopping-cart"></i>
                            <span className="cart-count">{cartItems.length}</span>
                        </button>
                        <button className="menu-toggle" onClick={toggleMenu}>
                            <i className="fas fa-bars"></i>
                        </button>
                    </div>
                </div>
            </div>
        </header>
    );
}

export default Header;
