import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/menu.css';
import { fetchMenuItems } from '../services/menuService';
import { useCart } from '../context/CartContext';
import { Slider, InputNumber } from 'antd'; // Thêm thư viện antd cho slider
import 'antd/dist/antd.css'; // Import CSS cho antd

function Menu() {
    const [fruitItems, setFruitItems] = useState([]);
    const [seafoodItems, setSeafoodItems] = useState([]);
    const [meatItems, setMeatItems] = useState([]);
    const [nutItems, setNutItems] = useState([]);
    const [allItems, setAllItems] = useState([]);
    const [filteredItems, setFilteredItems] = useState([]);
    const [categories, setCategories] = useState({
        all: true,
        fruit: false,
        seafood: false,
        meat: false,
        nut: false,
    });
    const [sortOption, setSortOption] = useState('default');
    const [priceRange, setPriceRange] = useState([0, 500000]); // Khoảng giá mặc định
    const { addToCart } = useCart();
    const navigate = useNavigate();

    // Fetch dữ liệu từ API
    useEffect(() => {
        fetchMenuItems('fruit').then((items) => {
            setFruitItems(items);
            setAllItems((prev) => [...prev, ...items.map(item => ({ ...item, category: 'fruit' }))]);
        });
        fetchMenuItems('seafood').then((items) => {
            setSeafoodItems(items);
            setAllItems((prev) => [...prev, ...items.map(item => ({ ...item, category: 'seafood' }))]);
        });
        fetchMenuItems('meat').then((items) => {
            setMeatItems(items);
            setAllItems((prev) => [...prev, ...items.map(item => ({ ...item, category: 'meat' }))]);
        });
        fetchMenuItems('nut').then((items) => {
            setNutItems(items);
            setAllItems((prev) => [...prev, ...items.map(item => ({ ...item, category: 'nut' }))]);
        });
    }, []);

    // Lọc và sắp xếp sản phẩm
    useEffect(() => {
        let itemsToFilter = [...allItems];

        // Lọc theo danh mục
        if (!categories.all) {
            itemsToFilter = itemsToFilter.filter(item => {
                return (
                    (categories.fruit && item.category === 'fruit') ||
                    (categories.seafood && item.category === 'seafood') ||
                    (categories.meat && item.category === 'meat') ||
                    (categories.nut && item.category === 'nut')
                );
            });
        }

        // Lọc theo khoảng giá
        itemsToFilter = itemsToFilter.filter(item => {
            const price = item.prices?.["0"]?.["25"] || item.prices?.["1"] || 0;
            return price >= priceRange[0] && price <= priceRange[1];
        });

        // Sắp xếp theo giá
        if (sortOption === 'low-to-high') {
            itemsToFilter.sort((a, b) => {
                const priceA = a.prices?.["0"]?.["25"] || a.prices?.["1"] || 0;
                const priceB = b.prices?.["0"]?.["25"] || b.prices?.["1"] || 0;
                return priceA - priceB;
            });
        } else if (sortOption === 'high-to-low') {
            itemsToFilter.sort((a, b) => {
                const priceA = a.prices?.["0"]?.["25"] || a.prices?.["1"] || 0;
                const priceB = b.prices?.["0"]?.["25"] || b.prices?.["1"] || 0;
                return priceB - priceA;
            });
        }

        setFilteredItems(itemsToFilter);
    }, [allItems, categories, sortOption, priceRange]);

    // Xử lý thay đổi checkbox danh mục
    const handleCategoryChange = (category) => {
        if (category === 'all') {
            setCategories({
                all: true,
                fruit: false,
                seafood: false,
                meat: false,
                nut: false,
            });
        } else {
            setCategories(prev => {
                const newState = {
                    ...prev,
                    [category]: !prev[category],
                    all: false,
                };
                
                // Nếu tất cả các checkbox khác đều false thì chọn 'all'
                if (!newState.fruit && !newState.seafood && !newState.meat && !newState.nut) {
                    return {
                        all: true,
                        fruit: false,
                        seafood: false,
                        meat: false,
                        nut: false,
                    };
                }
                return newState;
            });
        }
    };

    // Xử lý thay đổi sắp xếp
    const handleSortChange = (e) => {
        setSortOption(e.target.value);
    };

    // Xử lý thay đổi khoảng giá
    const handlePriceChange = (value) => {
        setPriceRange(value);
    };

    // Xử lý thêm vào giỏ hàng
    const handleAddToCart = (item) => {
        const price = item.prices?.["0"]?.["25"] || item.prices?.["1"];
        if (price === undefined) {
            console.error("Lỗi: Không tìm thấy giá sản phẩm", item);
            return;
        }

        addToCart({
            productId: item._id,
            name: item.name,
            price: price,
            image: item.image,
            size: "0.25",
            quantity: 1,
        });
    };

    // Render sản phẩm
    const renderMenuItems = () => (
        <div className="menu-lists">
            {filteredItems.length > 0 ? (
                filteredItems.map((item) => {
                    const currentPrice = item.prices?.["0"]?.["25"] || item.prices?.["1"];
                    const originalPrice = currentPrice / (1 - 0.17); // Giảm 17%
                    const discountPercentage = 17;

                    return (
                        <div className="food-items" key={item._id}>
                            <div
                                className="food-item"
                                onClick={() => navigate(`/${item.category}/${item._id}`)}
                                style={{ cursor: "pointer" }}
                            >
                                <img src={item.image} alt={item.name} />
                                <h2>{item.name}</h2>
                            </div>
                            <div className="food-price">
                                <span className="current-price">
                                    {currentPrice
                                        ? `${currentPrice.toLocaleString()} VND`
                                        : "Giá chưa cập nhật"}
                                </span>
                                {originalPrice && (
                                    <span className="original-price">
                                        {Math.round(originalPrice).toLocaleString()} VND
                                    </span>
                                )}
                                {discountPercentage && (
                                    <span className="discount">-{discountPercentage}%</span>
                                )}
                            </div>
                            <div className="food-meta">
                                <span>⭐ {item.rating || 5.0}</span>
                                <span>Đã bán {item.sold || 0}</span>
                            </div>
                            <button
                                className="add-to-cart-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    handleAddToCart(item);
                                }}
                            >
                                🛒
                            </button>
                        </div>
                    );
                })
            ) : (
                <div className="no-products">
                    <p>Không có sản phẩm nào phù hợp với bộ lọc hiện tại</p>
                </div>
            )}
        </div>
    );

    return (
        <section className="menu" id="dactrung">
            <div className="menu-wrapper">
                {/* Sidebar danh mục */}
                <div className="menu-sidebar">
                    <h3>Danh Mục</h3>
                    <div className="category-checkbox">
                        <label>
                            <input
                                type="checkbox"
                                checked={categories.all}
                                onChange={() => handleCategoryChange('all')}
                            />
                            Tất cả sản phẩm
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={categories.fruit}
                                onChange={() => handleCategoryChange('fruit')}
                            />
                            Trái Cây Sấy
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={categories.seafood}
                                onChange={() => handleCategoryChange('seafood')}
                            />
                            Hải Sản Sấy
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={categories.meat}
                                onChange={() => handleCategoryChange('meat')}
                            />
                            Thịt Sấy
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={categories.nut}
                                onChange={() => handleCategoryChange('nut')}
                            />
                            Hạt Sấy
                        </label>
                    </div>

                    {/* Bộ lọc theo giá */}
                    <div className="price-filter">
                        <h3>Khoảng giá (VND)</h3>
                        <div style={{ display: 'flex', gap: '10px', marginBottom: '16px' }}>
                            <InputNumber
                                value={priceRange[0]}
                                onChange={(value) => setPriceRange([value, priceRange[1]])}
                                min={0}
                                max={priceRange[1]}
                                formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                                parser={value => value.replace(/\$\s?|(,*)/g, '')}
                            />
                            <InputNumber
                                value={priceRange[1]}
                                onChange={(value) => setPriceRange([priceRange[0], value])}
                                min={priceRange[0]}
                                max={1000000}
                                formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                                parser={value => value.replace(/\$\s?|(,*)/g, '')}
                            />
                        </div>
                        <Slider
                            range
                            min={0}
                            max={1000000}
                            step={10000}
                            value={priceRange}
                            onChange={handlePriceChange}
                            tipFormatter={value => `${value.toLocaleString()} VND`}
                        />
                    </div>
                </div>

                {/* Phần nội dung chính */}
                <div className="menu-main-content">
                    {/* Tiêu đề */}
                    <div className="section-title">
                        <h2 data-title="">SẢN PHẨM CỦA CHÚNG TÔI</h2>
                    </div>

                    {/* Tagline */}
                    <div className="menu-title">
                        <h2>Tận hưởng vị ngon tự nhiên từ thực phẩm sấy khô...</h2>
                    </div>

                    {/* Phần sản phẩm */}
                    <div className="menu-products">
                        {/* Sắp xếp */}
                        <div className="menu-sort">
                            <select value={sortOption} onChange={handleSortChange}>
                                <option value="default">Sắp xếp: Mặc định</option>
                                <option value="low-to-high">Giá: Thấp đến cao</option>
                                <option value="high-to-low">Giá: Cao đến thấp</option>
                            </select>
                        </div>

                        {/* Hiển thị sản phẩm */}
                        {renderMenuItems()}
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Menu;
