// context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    // Kiểm tra token khi ứng dụng khởi động
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axios.get('http://localhost:5000/api/users/me', {
                headers: { Authorization: `Bearer ${token}` }
            })
                .then(response => {
                    setUser(response.data); // Lưu thông tin user (bao gồm userId)
                    setLoading(false);
                })
                .catch(error => {
                    console.error('Lỗi khi lấy thông tin user:', error);
                    localStorage.removeItem('token');
                    setUser(null);
                    setLoading(false);
                });
        } else {
            setLoading(false);
        }
    }, []);

    const login = async (email, password) => {
        try {
            const response = await axios.post('http://localhost:5000/api/users/login', { email, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token); // Lưu token vào localStorage
            setUser(user); // Lưu thông tin user (bao gồm userId)
        } catch (error) {
            throw error;
        }
    };

    const register = async (username, email, phone, password) => {
        try {
            const response = await axios.post('http://localhost:5000/api/users/register', { username, email, phone, password });
            const { token, user } = response.data;
            localStorage.setItem('token', token); // Lưu token vào localStorage
            setUser(user); // Lưu thông tin user (bao gồm userId)
        } catch (error) {
            throw error;
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setUser(null);
    };

    return (
        <AuthContext.Provider value={{ user, loading, login, register, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
