// components/ShippingAddressForm.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddressService from '../services/AddressService';
import { useAuth } from '../context/AuthContext';
import '../styles/ShippingAddressForm.css';

function ShippingAddressForm({ onAddressSelect }) {
    const { user, logout } = useAuth();
    const [formData, setFormData] = useState({
        province: '',
        district: '',
        ward: '',
        detail: '',
        fullName: '',
        phone: ''
    });
    const [errors, setErrors] = useState({});
    const [savedAddresses, setSavedAddresses] = useState([]);
    const [selectedAddressId, setSelectedAddressId] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [showForm, setShowForm] = useState(false);
    const [provinces, setProvinces] = useState([]);
    const [districts, setDistricts] = useState([]);
    const [wards, setWards] = useState([]);

    // Hàm kiểm tra dữ liệu (validation)
    const validateForm = (data) => {
        const newErrors = {};

        if (!data.fullName.trim()) {
            newErrors.fullName = 'Họ và tên không được để trống.';
        }

        const phoneRegex = /^[0-9]{10}$/;
        if (!data.phone.trim()) {
            newErrors.phone = 'Số điện thoại không được để trống.';
        } else if (!phoneRegex.test(data.phone.trim())) {
            newErrors.phone = 'Số điện thoại không hợp lệ (phải là 10 chữ số).';
        }

        if (!data.province) {
            newErrors.province = 'Vui lòng chọn tỉnh/thành phố.';
        }

        if (!data.district) {
            newErrors.district = 'Vui lòng chọn quận/huyện.';
        }

        if (!data.ward) {
            newErrors.ward = 'Vui lòng chọn phường/xã.';
        }

        if (!data.detail.trim()) {
            newErrors.detail = 'Địa chỉ chi tiết không được để trống.';
        }

        return newErrors;
    };

    // Lấy danh sách tỉnh/thành phố từ API
    useEffect(() => {
        const fetchProvinces = async () => {
            try {
                const response = await axios.get('https://provinces.open-api.vn/api/p/');
                setProvinces(response.data);
            } catch (error) {
                console.error('Lỗi khi lấy danh sách tỉnh:', error);
                setErrors({ form: 'Lỗi khi tải danh sách tỉnh/thành phố.' });
            }
        };
        fetchProvinces();
    }, []);

    // Lấy danh sách quận/huyện khi tỉnh thay đổi
    useEffect(() => {
        if (formData.province) {
            const fetchDistricts = async () => {
                try {
                    const response = await axios.get(`https://provinces.open-api.vn/api/p/${formData.province}?depth=2`);
                    setDistricts(response.data.districts || []);
                    setFormData((prev) => ({ ...prev, district: '', ward: '' }));
                    setWards([]);
                    setErrors((prev) => validateForm({ ...formData, district: '', ward: '' }));
                } catch (error) {
                    console.error('Lỗi khi lấy danh sách quận/huyện:', error);
                    setErrors({ form: 'Lỗi khi tải danh sách quận/huyện.' });
                }
            };
            fetchDistricts();
        } else {
            setDistricts([]);
            setWards([]);
        }
    }, [formData.province]);

    // Lấy danh sách phường/xã khi quận/huyện thay đổi
    useEffect(() => {
        if (formData.district) {
            const fetchWards = async () => {
                try {
                    const response = await axios.get(`https://provinces.open-api.vn/api/d/${formData.district}?depth=2`);
                    setWards(response.data.wards || []);
                    setFormData((prev) => ({ ...prev, ward: '' }));
                    setErrors((prev) => validateForm({ ...formData, ward: '' }));
                } catch (error) {
                    console.error('Lỗi khi lấy danh sách phường/xã:', error);
                    setErrors({ form: 'Lỗi khi tải danh sách phường/xã.' });
                }
            };
            fetchWards();
        } else {
            setWards([]);
        }
    }, [formData.district]);

    // Lấy danh sách địa chỉ từ API
    useEffect(() => {
        const fetchAddresses = async () => {
            if (user) {
                try {
                    const addresses = await AddressService.getAddresses();
                    setSavedAddresses(addresses);
                    if (addresses.length > 0) {
                        onAddressSelect({
                            fullName: addresses[0].fullName,
                            address: `${addresses[0].detail}, ${addresses[0].ward}, ${addresses[0].district}, ${addresses[0].province}`,
                            phone: addresses[0].phone
                        });
                    } else {
                        onAddressSelect(null);
                    }
                } catch (error) {
                    if (error.response && error.response.status === 401) {
                        setErrors({ form: 'Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.' });
                        logout();
                    } else {
                        setSavedAddresses([]);
                        setErrors({ form: 'Lỗi khi tải danh sách địa chỉ.' });
                    }
                }
            }
        };
        fetchAddresses();
    }, [user, logout, onAddressSelect]);

    // Xử lý thay đổi input và validation realtime
    const handleInputChange = (field, value) => {
        const updatedFormData = { ...formData, [field]: value };
        setFormData(updatedFormData);
        const newErrors = validateForm(updatedFormData);
        setErrors(newErrors);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const validationErrors = validateForm(formData);
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
            return;
        }

        const provinceName = provinces.find(p => p.code === parseInt(formData.province))?.name || '';
        const districtName = districts.find(d => d.code === parseInt(formData.district))?.name || '';
        const wardName = wards.find(w => w.code === parseInt(formData.ward))?.name || '';
        const addressData = {
            fullName: formData.fullName,
            phone: formData.phone,
            province: provinceName,
            district: districtName,
            ward: wardName,
            detail: formData.detail
        };

        try {
            if (isEditing && selectedAddressId) {
                const updatedAddress = await AddressService.updateAddress(selectedAddressId, addressData);
                setSavedAddresses((prev) =>
                    prev.map(addr => (addr._id === selectedAddressId ? updatedAddress : addr))
                );
                setIsEditing(false);
                if (selectedAddressId === savedAddresses[0]._id) {
                    onAddressSelect({
                        fullName: updatedAddress.fullName,
                        address: `${updatedAddress.detail}, ${updatedAddress.ward}, ${updatedAddress.district}, ${updatedAddress.province}`,
                        phone: updatedAddress.phone
                    });
                }
            } else {
                const newAddress = await AddressService.addAddress(addressData);
                setSavedAddresses((prev) => [newAddress, ...prev]);
                onAddressSelect({
                    fullName: newAddress.fullName,
                    address: `${newAddress.detail}, ${newAddress.ward}, ${newAddress.district}, ${newAddress.province}`,
                    phone: newAddress.phone
                });
            }

            setFormData({
                province: '',
                district: '',
                ward: '',
                detail: '',
                fullName: '',
                phone: ''
            });
            setSelectedAddressId(null);
            setErrors({});
            setShowForm(false);
        } catch (error) {
            console.error('Lỗi khi lưu địa chỉ:', error);
            if (error.response && error.response.status === 401) {
                setErrors({ form: 'Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.' });
                logout();
            } else {
                setErrors({ form: error.response?.data?.message || 'Lỗi khi lưu địa chỉ.' });
            }
        }
    };

    const handleEdit = async (addressId) => {
        const addressToEdit = savedAddresses.find(addr => addr._id === addressId);
        if (addressToEdit) {
            // Đặt formData với dữ liệu của địa chỉ cần chỉnh sửa
            const provinceCode = provinces.find(p => p.name === addressToEdit.province)?.code || '';
            setFormData({
                province: provinceCode,
                district: '',
                ward: '',
                detail: addressToEdit.detail,
                fullName: addressToEdit.fullName,
                phone: addressToEdit.phone
            });

            // Tải lại danh sách quận/huyện và phường/xã
            if (provinceCode) {
                try {
                    const districtResponse = await axios.get(`https://provinces.open-api.vn/api/p/${provinceCode}?depth=2`);
                    setDistricts(districtResponse.data.districts || []);
                    const districtCode = districtResponse.data.districts.find(d => d.name === addressToEdit.district)?.code || '';
                    setFormData((prev) => ({ ...prev, district: districtCode }));

                    if (districtCode) {
                        const wardResponse = await axios.get(`https://provinces.open-api.vn/api/d/${districtCode}?depth=2`);
                        setWards(wardResponse.data.wards || []);
                        const wardCode = wardResponse.data.wards.find(w => w.name === addressToEdit.ward)?.code || '';
                        setFormData((prev) => ({ ...prev, ward: wardCode }));
                    }
                } catch (error) {
                    console.error('Lỗi khi tải dữ liệu để chỉnh sửa:', error);
                    setErrors({ form: 'Lỗi khi tải dữ liệu để chỉnh sửa.' });
                }
            }

            setSelectedAddressId(addressId);
            setIsEditing(true);
            setShowForm(true);
            setErrors({});
        }
    };

    const handleDelete = async (addressId) => {
        try {
            await AddressService.deleteAddress(addressId);
            const updatedAddresses = savedAddresses.filter(addr => addr._id !== addressId);
            setSavedAddresses(updatedAddresses);
            if (addressId === savedAddresses[0]._id && updatedAddresses.length > 0) {
                onAddressSelect({
                    fullName: updatedAddresses[0].fullName,
                    address: `${updatedAddresses[0].detail}, ${updatedAddresses[0].ward}, ${updatedAddresses[0].district}, ${updatedAddresses[0].province}`,
                    phone: updatedAddresses[0].phone
                });
            } else if (updatedAddresses.length === 0) {
                onAddressSelect(null);
            }
        } catch (error) {
            if (error.response && error.response.status === 401) {
                setErrors({ form: 'Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.' });
                logout();
            } else {
                setErrors({ form: 'Lỗi khi xóa địa chỉ.' });
            }
        }
    };

    return (
        <div className="shipping-address-container">
            <h2>Địa chỉ giao hàng</h2>
            {user && <p className="user-id">User ID: {user.id}</p>}

            {savedAddresses.length > 0 ? (
                <div className="default-address">
                    <h3>Địa chỉ mặc định</h3>
                    <p><strong>Họ và tên:</strong> {savedAddresses[0].fullName}</p>
                    <p><strong>Địa chỉ:</strong> {`${savedAddresses[0].detail}, ${savedAddresses[0].ward}, ${savedAddresses[0].district}, ${savedAddresses[0].province}`}</p>
                    <p><strong>Số điện thoại:</strong> {savedAddresses[0].phone}</p>
                </div>
            ) : (
                <div className="no-address">
                    <p>Chưa có địa chỉ giao hàng.</p>
                    <button onClick={() => setShowForm(true)} className="add-address-btn">
                        Thêm địa chỉ
                    </button>
                </div>
            )}

            {savedAddresses.length > 1 && (
                <div className="other-addresses">
                    <h3>Các địa chỉ khác</h3>
                    {savedAddresses.slice(1).map((address) => (
                        <div key={address._id} className="address-item">
                            <p>{`${address.fullName}, ${address.detail}, ${address.ward}, ${address.district}, ${address.province}`}</p>
                            <p>{address.phone}</p>
                            <button onClick={() => handleEdit(address._id)} className="edit-btn">Sửa</button>
                            <button onClick={() => handleDelete(address._id)} className="delete-btn">Xóa</button>
                        </div>
                    ))}
                </div>
            )}

            {savedAddresses.length > 0 && !showForm && (
                <button onClick={() => setShowForm(true)} className="add-address-btn">
                    Thêm địa chỉ mới
                </button>
            )}

            {showForm && (
                <form onSubmit={handleSubmit} className="address-form">
                    <div className="form-group">
                        <label>Họ và tên:</label>
                        <input
                            type="text"
                            value={formData.fullName}
                            onChange={(e) => handleInputChange('fullName', e.target.value)}
                            className={errors.fullName ? 'input-error' : ''}
                        />
                        {errors.fullName && <p className="error-message">{errors.fullName}</p>}
                    </div>

                    <div className="form-group">
                        <label>Số điện thoại:</label>
                        <input
                            type="text"
                            value={formData.phone}
                            onChange={(e) => handleInputChange('phone', e.target.value)}
                            className={errors.phone ? 'input-error' : ''}
                        />
                        {errors.phone && <p className="error-message">{errors.phone}</p>}
                    </div>

                    <div className="form-group">
                        <label>Tỉnh/Thành phố:</label>
                        <select
                            value={formData.province}
                            onChange={(e) => handleInputChange('province', e.target.value)}
                            className={errors.province ? 'input-error' : ''}
                        >
                            <option value="">Chọn Tỉnh/Thành phố</option>
                            {provinces.map((province) => (
                                <option key={province.code} value={province.code}>
                                    {province.name}
                                </option>
                            ))}
                        </select>
                        {errors.province && <p className="error-message">{errors.province}</p>}
                    </div>

                    <div className="form-group">
                        <label>Quận/Huyện:</label>
                        <select
                            value={formData.district}
                            onChange={(e) => handleInputChange('district', e.target.value)}
                            className={errors.district ? 'input-error' : ''}
                            disabled={!formData.province}
                        >
                            <option value="">Chọn Quận/Huyện</option>
                            {districts.map((district) => (
                                <option key={district.code} value={district.code}>
                                    {district.name}
                                </option>
                            ))}
                        </select>
                        {errors.district && <p className="error-message">{errors.district}</p>}
                    </div>

                    <div className="form-group">
                        <label>Phường/Xã:</label>
                        <select
                            value={formData.ward}
                            onChange={(e) => handleInputChange('ward', e.target.value)}
                            className={errors.ward ? 'input-error' : ''}
                            disabled={!formData.district}
                        >
                            <option value="">Chọn Phường/Xã</option>
                            {wards.map((ward) => (
                                <option key={ward.code} value={ward.code}>
                                    {ward.name}
                                </option>
                            ))}
                        </select>
                        {errors.ward && <p className="error-message">{errors.ward}</p>}
                    </div>

                    <div className="form-group">
                        <label>Địa chỉ chi tiết:</label>
                        <input
                            type="text"
                            value={formData.detail}
                            onChange={(e) => handleInputChange('detail', e.target.value)}
                            className={errors.detail ? 'input-error' : ''}
                        />
                        {errors.detail && <p className="error-message">{errors.detail}</p>}
                    </div>

                    {errors.form && <p className="form-error">{errors.form}</p>}
                    <div className="form-buttons">
                        <button type="submit" className="submit-btn">
                            {isEditing ? 'Cập nhật' : 'Thêm địa chỉ'}
                        </button>
                        <button
                            type="button"
                            onClick={() => {
                                setShowForm(false);
                                setFormData({ province: '', district: '', ward: '', detail: '', fullName: '', phone: '' });
                                setErrors({});
                                setIsEditing(false);
                                setSelectedAddressId(null);
                            }}
                            className="cancel-btn"
                        >
                            Hủy
                        </button>
                    </div>
                </form>
            )}
        </div>
    );
}

export default ShippingAddressForm;
