import React, { useEffect } from 'react';
import '../styles/cartsidebar.css';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

function CartSidebar() {
    const {
        cartItems,
        removeFromCart,
        isOpen,
        toggleCart,
        total,
        totalItems,
        increaseQuantity,
        decreaseQuantity,
        fetchCartFromServer,
        loadCartFromLocalStorage
    } = useCart();

    const { user } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (user && user._id) {
            fetchCartFromServer(user._id);
        } else {
            loadCartFromLocalStorage();
        }
    }, [user, fetchCartFromServer, loadCartFromLocalStorage]);

    const handleViewCart = () => {
        navigate('/cart');
    };

    const handleViewProduct = (productId, category) => {
        const productPath = category ? `/${category}/${productId}` : `/product/${productId}`;
        navigate(productPath);
    };

    return (
        <>
            <div className="cart-toggle-btn" onClick={toggleCart}>
                <i className="fas fa-shopping-cart"></i>
                <span className="cart-count-badge">{totalItems}</span>
            </div>

            <div id="cart-sidebar" className={isOpen ? 'open' : 'closed'}>
                <div className="cart-header">
                    <h2>
                        <i className="fas fa-shopping-cart" style={{ marginRight: '8px' }}></i>
                        Giỏ hàng
                    </h2>
                    <button className="close-btn" onClick={toggleCart}>
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <div className="cart-actions">
                    <p id="cart-count">Tổng số lượng: {totalItems}</p>
                </div>
                <ul id="cart-items">
                    {cartItems.length === 0 ? (
                        <li className="cart-item">
                            <p style={{ textAlign: 'center', color: '#1a2b49', width: '100%' }}>
                                Giỏ hàng của bạn đang trống.
                            </p>
                        </li>
                    ) : (
                        cartItems.map((item, index) => (
                            <li key={index} className="cart-item">
                                <div
                                    className="cart-item-details"
                                    onClick={() => handleViewProduct(item.productId, item.category)}
                                    style={{ cursor: 'pointer' }}
                                >
                                    <img src={item.image} alt={item.name} className="cart-item-image" />
                                    <div className="cart-item-info">
                                        <span>
                                            {typeof item.name === 'string'
                                                ? item.name
                                                : "Tên sản phẩm không hợp lệ"}
                                        </span>
                                        <span>
                                            {typeof item.price === 'number'
                                                ? `${item.price.toLocaleString()} VND`
                                                : "Chưa có giá"}
                                        </span>
                                        <div className="quantity-controls">
                                            <button onClick={(e) => { e.stopPropagation(); decreaseQuantity(item.productId, item.size); }}>-</button>
                                            <span>{typeof item.quantity === 'number' ? item.quantity : 1}</span>
                                            <button onClick={(e) => { e.stopPropagation(); increaseQuantity(item.productId, item.size); }}>+</button>
                                        </div>
                                    </div>
                                </div>
                                <button onClick={() => removeFromCart(item.productId, item.size)}>Xóa</button>
                            </li>
                        ))
                    )}
                </ul>
                <div className="cart-footer">
                    <p>
                        Tổng tiền:
                        <span id="cart-total">
                            {typeof total === 'number' ? `${total.toLocaleString()} VND` : "Không xác định"}
                        </span>
                    </p>
                    <button id="view-cart-btn" onClick={handleViewCart}>
                        Xem giỏ hàng
                    </button>
                </div>
            </div>
        </>
    );
}

export default CartSidebar;
