import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import ShippingAddressForm from "./ShippingAddressForm";
import "../styles/Payment.css";

const Payment = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState(state.selectedAddress || null);

  useEffect(() => {
    if (!state || (!state.selectedItems && !state.item) || !state.total || state.total <= 0) {
      setError("Không có sản phẩm nào để thanh toán. Vui lòng quay lại giỏ hàng.");
      setTimeout(() => navigate("/cart"), 3000);
      return;
    }
    if (!user || !user._id) {
      setError("Vui lòng đăng nhập để thanh toán.");
      setTimeout(() => navigate("/sign-in"), 3000);
      return;
    }
    if (!selectedAddress) {
      setShowAddressForm(true); // Hiển thị form nếu chưa có địa chỉ
    }
  }, [state, user, navigate, selectedAddress]);

  const items = state.selectedItems || (state.item ? [state.item] : []);
  const total = state.total;

  const createOrderInfo = (items) => {
    return items
      .map((item) => `${item.name} ${item.size} - ${item.quantity} x ${item.price}`)
      .join(", ");
  };

  const handlePayment = async () => {
    if (!items.length || !total || !user || !user._id || !selectedAddress) {
      setError("Vui lòng chọn địa chỉ giao hàng hoặc dữ liệu không hợp lệ.");
      return;
    }

    const orderInfo = createOrderInfo(items);
    const amount = total;

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post("http://localhost:5001/create_payment", {
        userId: user._id,
        amount: amount,
        orderInfo: orderInfo,
        items: items,
        address: selectedAddress,
      });

      if (response.data.status === "success") {
        window.location.href = response.data.url;
      } else {
        setError("Không thể tạo URL thanh toán. Vui lòng thử lại.");
      }
    } catch (error) {
      console.error("Lỗi khi tạo URL thanh toán:", error);
      setError("Đã có lỗi xảy ra. Vui lòng thử lại!");
    } finally {
      setLoading(false);
    }
  };

  const handleAddressSelect = (address) => {
    setSelectedAddress(address);
    setShowAddressForm(false);
  };

  if (!items.length || !total) {
    return (
      <div className="payment-container">
        <h2>Thanh toán</h2>
        <p className="error-message">{error || "Đang tải..."}</p>
      </div>
    );
  }

  return (
    <div className="payment-container with-background">
      <h2>Thanh toán</h2>

      <div className="order-summary">
        <h3>Thông tin đơn hàng</h3>
        <div className="order-items">
          {items.map((item) => (
            <div key={`${item.productId}-${item.size}`} className="order-item">
              <img src={item.image} alt={item.name} className="order-item-image" />
              <div className="order-item-details">
                <p><strong>{item.name}</strong></p>
                <p>Phân loại: {item.size}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Đơn giá: {item.price.toLocaleString()}đ</p>
                <p>Tổng: {(item.price * item.quantity).toLocaleString()}đ</p>
              </div>
            </div>
          ))}
        </div>
        <div className="order-total">
          <p>Tổng thanh toán ({items.length} sản phẩm): <strong>{total.toLocaleString()}đ</strong></p>
        </div>
      </div>

      <div className="shipping-address">
        <h3>Địa chỉ giao hàng</h3>
        {selectedAddress ? (
          <div>
            <p><strong>{selectedAddress.fullName}</strong> ({selectedAddress.phone})</p>
            <p>{selectedAddress.address}</p>
            <button onClick={() => setShowAddressForm(true)} className="change-address-btn">
              Thay đổi địa chỉ
            </button>
          </div>
        ) : (
          <p>Chưa có địa chỉ giao hàng.</p>
        )}
      </div>

      {showAddressForm && (
        <ShippingAddressForm
          onAddressSelect={handleAddressSelect}
          onClose={() => setShowAddressForm(false)}
        />
      )}

      <div className="payment-actions">
        {error && <p className="error-message">{error}</p>}
        <button
          onClick={handlePayment}
          disabled={loading || !selectedAddress}
          className="payment-btn"
        >
          {loading ? "Đang xử lý..." : "Thanh toán với VNPay"}
        </button>
      </div>
    </div>
  );
};

export default Payment;
