import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../styles/sign-in.css";

function SignIn() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const { login } = useAuth(); // Lấy hàm login từ AuthContext
    const navigate = useNavigate(); // Khởi tạo useNavigate để điều hướng

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const res = await fetch('http://localhost:5000/api/users/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            const data = await res.json();

            if (res.ok) {
                // API trả về { message: "Đăng nhập thành công!", user: { _id, username, email, phone } }
                const userData = {
                    _id: data.user._id, // Lấy _id từ response
                    username: data.user.username,
                    email: data.user.email,
                    phone: data.user.phone
                };
                login(userData); // Lưu thông tin người dùng (bao gồm _id) vào AuthContext
                alert("Đăng nhập thành công!");
                navigate("/"); // Chuyển hướng về trang chủ
            } else {
                // Xử lý lỗi từ backend (ví dụ: "Email không tồn tại", "Mật khẩu không đúng")
                setError(data.message || "Đăng nhập thất bại!");
            }
        } catch (err) {
            console.error("Lỗi khi gọi API đăng nhập:", err);
            setError("Có lỗi xảy ra, vui lòng thử lại!");
        }
    };

    return (
        <section className="sign-in">
            <h1 className="sign-in-heading">Đăng nhập</h1>
            {error && <p className="error-message">{error}</p>}

            <form onSubmit={handleSubmit} className="sign-in-form">
                <label htmlFor="email" className="sign-in-label">Email</label>
                <input
                    id="email"
                    type="email"
                    className="sign-in-input"
                    placeholder="Nhập email của bạn"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <label htmlFor="password" className="sign-in-label">Mật Khẩu</label>
                <input
                    id="password"
                    type="password"
                    className="sign-in-input"
                    placeholder="Nhập mật khẩu của bạn"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                <button type="submit" className="sign-in-submit">Đăng nhập</button>
            </form>

            <p className="sign-in-already">
                <span>Bạn chưa có tài khoản?</span>
                <a href="/sign-up" className="sign-in-login">Đăng ký</a>
            </p>
        </section>
    );
}

export default SignIn;
