import React from 'react';
import '../styles/style.css'; // Đảm bảo import file CSS của bạn

function Review() {
    // Dữ liệu đánh giá từ khách hàng
    const reviews = [
        {
            text: 'Gà xiên nướng thơm phức, vị đậm đà nhưng không quá mặn, kết hợp hoàn hảo với rau củ tươi mát. Thịt gà mềm, không bị khô, rất vừa miệng. Món này vừa ngon vừa đẹp mắt, chắc chắn sẽ quay lại để thưởng thức lần nữa',
            name: 'Trần Duy Khánh',
            date: 'Ngày 06/11/2024',
            image: '../images/khanh.jpg',
        },
        {
            text: 'Gỏi đu đủ có vị chua cay rất kích thích, ăn vào giòn sần sật và tươi mát. Đu đủ và rau sống đều tươi ngon, kết hợp với tôm và đậu phộng tạo cảm giác béo bùi. Đây là món khai vị hoàn hảo cho một bữa ăn ngon miệng.',
            name: 'Lưu Đức Quang',
            date: 'Ngày 06/11/2024',
            image: '../images/quang.jpg',
        },
        {
            text: 'Pad Thái đúng chuẩn hương vị Thái Lan, sợi mì dai mềm và ngấm gia vị vừa phải. Vị chua nhẹ của chanh và mùi thơm của tôm làm món ăn thêm phần hấp dẫn. Món này rất hợp cho những ai yêu thích ẩm thực Thái',
            name: 'Võ Mạnh Hoàng',
            date: 'Ngày 06/11/2024',
            image: '../images/mhoang.jpg',
        },
    ];

    return (
        <section className="review" id="feedback">
            <div className="container">
                <div className="section-title" data-aos="fade-up-right" data-aos-duration="1500">
                    <h2 data-title="OUR STORY">Phản Hồi</h2>
                </div>
                <h1 data-aos="fade-left" data-aos-duration="1500">
                    Đối với chúng tôi, chất lượng sản phẩm là trái tim, là linh hồn của Thai Market
                </h1>
                <div className="review-content row">
                    {reviews.map((review, index) => (
                        <div className="review-item" key={index}>
                            <div className="review-item-text">
                                <img src="../images/qutes.png" alt="Quotes Icon" />
                                <p>
                                    <span>“</span>
                                    {review.text}
                                    <span>“</span>
                                </p>
                            </div>
                            <div className="review-item-img row">
                                <img src={review.image} alt={review.name} />
                                <div className="review-item-img-text">
                                    <h2>{review.name}</h2>
                                    <p>{review.date}</p>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Review;
