// src/components/CartSidebar.jsx
import React from 'react';
import '../styles/cartsidebar.css';
import { useCart } from '../context/CartContext';

function CartSidebar() {
    const { cartItems, removeFromCart, isOpen, toggleCart, total } = useCart();

    return (
        <>
            <div className="cart-toggle-btn" onClick={toggleCart}>
                <i className="fas fa-shopping-cart"></i>
                <span className="cart-count-badge">{cartItems.length}</span>
            </div>

            <div id="cart-sidebar" className={isOpen ? 'open' : 'closed'}>
                <div className="cart-header">
                    <h2>Gi·ªè h√†ng üõí</h2>
                    <button className="close-btn" onClick={toggleCart}>
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <p id="cart-count">S·ªë l∆∞·ª£ng: {cartItems.length}</p>
                <ul id="cart-items">
                    {cartItems.map((item, index) => (
                        <li key={index}>
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <span>{item.name}</span>
                                <span>{item.price.toLocaleString()} VND</span>
                            </div>
                            <button onClick={() => removeFromCart(index)}>X√≥a</button>
                        </li>
                    ))}
                </ul>
                <p>T·ªïng ti·ªÅn: <span id="cart-total">{total.toLocaleString()} VND</span></p>
                <button id="checkout-btn" onClick={() => window.location.href = 'https://your-payment-link.com'}>
                    Thanh to√°n
                </button>
            </div>
        </>
    );
}

export default CartSidebar;
