import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import ProductItem from './ProductItem';
import RelatedItem from './RelatedItem';
import Footer from './Footer';
import CartSidebar from './CartSidebar'; // Thêm import CartSidebar
import { useCart } from '../context/CartContext';
import '../styles/ProductDetail.css';

const ProductDetail = () => {
    const { category, id } = useParams();
    const { addToCart } = useCart();
    const [product, setProduct] = useState(null);
    const [relatedProducts, setRelatedProducts] = useState([]);

    useEffect(() => {
        // Lấy thông tin sản phẩm chính
        fetch(`http://localhost:5000/api/products/${id}`)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => console.error('Lỗi tải sản phẩm:', err));

        // Lấy danh sách sản phẩm liên quan
        fetch(`http://localhost:5000/api/products?category=${category}`)
            .then(res => res.json())
            .then(data => {
                console.log('Dữ liệu từ API:', data); // Log để kiểm tra dữ liệu
                const filteredProducts = data.filter(p => p._id !== id);
                console.log('Sản phẩm liên quan:', filteredProducts); // Log để kiểm tra sau khi lọc
                setRelatedProducts(filteredProducts);
            })
            .catch(err => {
                console.error('Lỗi tải sản phẩm liên quan:', err);
                setRelatedProducts([]); // Đặt mảng rỗng nếu có lỗi
            });
    }, [category, id]);

    if (!product) {
        return <div>Đang tải sản phẩm...</div>;
    }

    return (
        <div className="product-detail">
            <Header />
            <div className="product-content">
                <ProductItem product={product} addToCart={addToCart} />
                <RelatedItem relatedProducts={relatedProducts} currentProductId={id} addToCart={addToCart} />
            </div>
            <CartSidebar /> {/* Thêm CartSidebar */}
            <Footer />
        </div>
    );
};

export default ProductDetail;
